    /************************************************************************************
        DataCollection description

        dsTcmSiteMngctChgDtlList: 현장별 상세내역(집계) GRID
        dsSearchMap: 검색조건 parameter
        dsTcmSiteMngctChgMap: 현장_관리비_변경 정보
     ************************************************************************************/

	 /************************************************************************************
	 	결재상태

		CM00401301	[미요청]		현장_결재상신
		CM00401302	[요청]			현장_결재승인, 공무팀_기안취소
		CM00401303	[공무팀결재중]	공무팀_결재상신
		CM00401304	[완료]			공무팀_결재승인
		CM00401305	[공무팀_반송]
		CM00401306	[공무팀_부결]
     ************************************************************************************/

    /************************************************************************************
     *  [1] 전역변수
     ************************************************************************************/
    //var svcId;      //callback 처리용 서비스ID

    /************************************************************************************
     *  [2] onpageload(초기화포함)
     ************************************************************************************/
    scwin.onpageload = function() {

    };

    scwin.onpageunload = function() {

    };

    /** 화면초기화 **/
    scwin.fnInitial = function() {
        //검색조건 셋팅
		dsSearchMap.set( "siteCd" , $w.getParameter("paramKeySiteCd") );//현장코드
		dsSearchMap.set( "exeYy" , $w.getParameter("paramKeyExeYy") );//실행년월
		dsSearchMap.set( "chgDgr" , $w.getParameter("paramKeyChgDgr") );//변경차수

		//헤더 TITLE변경
		grdAproDtlList.setHeaderValue( "column53" , dsSearchMap.get( "exeYy" ) + "기 집행금액");
		var bfYear = parseInt(dsSearchMap.get( "exeYy" ), 10)-1;//전년도
		grdAproDtlList.setHeaderValue( "column57" , "전년 누계실적<br/>(" + bfYear + ")" );

		btnApprRequest.setDisabled( true );//결재상신 button
		btnApprReject.setDisabled( true );//반송 button

       	scwin.fnTcmSiteMngctChg();
        //UI팀용 함수 호출
        com.commonUI();
    };

    /************************************************************************************
     *  [3] 사용자 정의함수 함수
     ************************************************************************************/
	//현장_관리비_변경 정보 조회
	scwin.fnTcmSiteMngctChg = function() {
        ajaxLib.ajax("/cm/cm1515/CM15150000U/selectTcmSiteMngctChg", {
            method : "POST",
            requestData : {
            				 tcmSiteMngctChg : dsSearchMap.getJSON()
            			},
            callback : //"fnCallback",
                function(result, e){
	                dsTcmSiteMngctChgMap.setJSON( result.data.dsTcmSiteMngctChgMap );

	                scwin.fnBtnControl();//상태값에 따른 버튼 제어

	                scwin.fnSearch();//현장관리비 내역 GRID 조회
            	},
            errCallback : "fnErrCallBack"
        });
	}

	//조회
    scwin.fnSearch = function() {
        ajaxLib.ajax("/cm/cm1515/CM15151010T/selectAproDtlList", {
            method : "POST",
            requestData : dsSearchMap.getJSON(),
            callback : //"fnCallback",
                function(result, e){
	                dsAproDtlList.setJSON(result.data.dsAproDtlList);
	                com.setUdcValue("dsTotalCount","udcListCount",dsAproDtlList.getTotalRow());   //GRID ROW COUNT

	                scwin.fnMergeRow();//행 병합

	                for(var i=0; i<grdAproDtlList.getTotalRow(); i++){
	                	if(dsAproDtlList.getCellData( i , "bgtItemiNm" ) == "소계") {
							grdAproDtlList.setRowBackgroundColor( i , "#F0E9F4" );
    					}else if(dsAproDtlList.getCellData( i , "bgtItemiNm" ) == "합계") {
    						grdAproDtlList.setRowBackgroundColor( i , "#F6F3F0" );
	                	}
					}//for end

            	},
            errCallback : "fnErrCallBack"
        });
    }

	//행 병합
	scwin.fnMergeRow = function() {
		var totalRow = grdAproDtlList.getTotalRow();
		var stndItemiNmIndex = dsAproDtlList.getColumnIndex("stndItemiNm");//분류
		grdAproDtlList.mergeCell({"rowIndex" : totalRow-3, "colIndex" : stndItemiNmIndex, "colSpan" : 3, "rowSpan" : 3});//분류, 내역 병합
	}

    //버튼 제어
	scwin.fnBtnControl = function() {
		//결재상태에 따라 버튼 제어 처리
		//결재번호 존재X [결재번호가 존재한다는 것은 결재 상신의 의미 기안 취소시 결재번호를 null로 update함]
		//[요청]	현장_결재승인, 공무팀_기안취소
		//[공무팀_부결]
		if( (dsTcmSiteMngctChgMap.get( "pbsvTeamAprvNo" ) == ""	&& dsTcmSiteMngctChgMap.get( "dirmgExctAproCd" ) == "CM00401302")
			|| dsTcmSiteMngctChgMap.get( "dirmgExctAproCd" ) == "CM00401306"
			){
			btnApprRequest.setDisabled( false );//결재상신 button
			btnApprReject.setDisabled( false );//반송 button
		}
	}

	//Font 색상 변경
	scwin.fnFontColor = function(value, formattedValue , rowIdx, colIdx) {
    	if(parseInt(value, 0) < 0) {
    		grdAproDtlList.setCellColor( rowIdx , colIdx , "red" );//음수
    	} else {
    		grdAproDtlList.setCellColor( rowIdx , colIdx , "black" );//양수
    	}
    	return formattedValue;
	};

    /************************************************************************************
     *  [4] Callback 함수
     ************************************************************************************/
    /** 오류시 처리 **/
    scwin.fnErrCallBack = function(result, e) {

    };

	//반송팝업 CALLBACK
	scwin.popApprRejectCallback = function(obj) {
		com.showMessage("SCN0001");//처리 되었습니다.
		scwin.fnInitial();
	}

    /************************************************************************************
     *  [5] Event 함수
     ************************************************************************************/
	//결재상신 클릭 이벤트
	scwin.btnApprRequest_onclick = function() {

		//결재본문 조회조건
		dsSrch.set( "siteCd" , dsTcmSiteMngctChgMap.get( "siteCd" ) );//현장코드
		dsSrch.set( "exeYy" , dsTcmSiteMngctChgMap.get( "exeYy" ) );//실행년도
		dsSrch.set( "chgDgr" , dsTcmSiteMngctChgMap.get( "chgDgr" ) );//변경차수
		dsSrch.set( "siteNm" , $w.getParameter("paramKeySiteNm") );//현장명

        var paramObj = new Object();
        paramObj.jobGbCd = "CM";                                         // 업무구분코드
        paramObj.importUrl = "/ux/cm/cm5500/CM55000035E.xml";            // 출력할 결재본문페이지
        paramObj.aprvTtl = dsTcmSiteMngctChgMap.get( "aprvTtl" );  		 // 결재제목
        paramObj.rvwrEmpno = "";        								 // 검토자_사번
        paramObj.fnlAprprEmpno = "";                               		 // 최종승인자_사번
        paramObj.aprvPrcgNm = "";                                        // 후처리결재처리명
        paramObj.aprvPrcgEventNm = "/cm/cmif/CMIfTelepiaPostProcess/notifyAprvCallSiteMngctPbsvTeam";// 후처리결재처리 이벤트명
        paramObj.aprvDocModiPsblYn = "Y";                                // 결재문서 수정불가기능 Y/N
        paramObj.aprlnModiPsblYn = "N";                                  // 결재선 수정불가기능 Y/N
        paramObj.aprvAtchModiPsblYn = "Y";                               // 결재첨부 수정불가기능 Y/N
        paramObj.aprvAflId = dsTcmSiteMngctChgMap.get( "aflId" );        // 본문링크용 첨부파일 id (콤머로 구분해서 보내준다)
        paramObj.popupWidth = "1350";                                    // 팝업창 가로크기
        paramObj.popupHeight = "800";                                    // 팝업창 세로크기
		paramObj.relatedDocumentIds = dsTcmSiteMngctChgMap.get( "gwDocNo" ); // 텔레피아 관련문서 ID (콤머로 구분해서 보내준다) : 필수값 아님

        com.aprvSendEditor(paramObj); //의견달기호출
	};

	//반송 클릭 이벤트
	scwin.btnApprReject_onclick = function() {
		var popOps = {
						  popup_name:"현장관리비예산승인 반송"
			           	, url:"/ux/cm/cm1515/CM15151025P.xml"
			           	, data:{
									 paramKeySiteCd : dsTcmSiteMngctChgMap.get( "siteCd" )	//현장코드
									,paramKeyExeYy  : dsTcmSiteMngctChgMap.get( "exeYy" )	//실행년도
									,paramKeyChgDgr : dsTcmSiteMngctChgMap.get( "chgDgr" )	//변경차수
			           	       }
			           	, callbackFn : "scwin.popApprRejectCallback"
			           	, widType:"M"
			           	, height:"400"
		             };
		com.popup_open(popOps);
	};

	//결재현황 클릭 이벤트
	scwin.btnSituation_onclick = function() {
		var popOps = {
						  popup_name:"결재현황"
			           	, url:"/ux/cm/cm1515/CM15150005P.xml"
			           	, data:{
									 paramKeySiteCd : dsTcmSiteMngctChgMap.get( "siteCd" )//현장코드
									,paramKeyExeYy : dsTcmSiteMngctChgMap.get( "exeYy" )//실행_년도
									,paramKeyChgDgr : dsTcmSiteMngctChgMap.get( "chgDgr" )//변경_차수
			           	       }
			           	, callbackFn : ""
			           	, widType:"XL"
			           	, height:"750"
		             };
		com.popup_open(popOps);
	};

	//엑셀 다운로드 이벤트
	scwin.btnExcelDwn_onclick = function() {
		var result = [];
		var totalCol = grdAproDtlList.getTotalCol();
		for(var i = 0; i < totalCol; i++) {
			if (!grdAproDtlList.getColumnVisible(i)) {
				result.push(i);
			}
		}
		var excelOpt = {
			 fileName: "현장별 상세내역(집계).xlsx"    //또는 xlsx 확장자 사용
			,removeColumns: result.join(",")
		    ,useSubTotal: true    			//colMerge된 컬럼을 Merge해서 출력 할 지 여부
		    ,colMergeTextAlign: "center"    //colMerge된 컬럼의 textAlign설정 (bottom, center, top 설정)
			,colMerge : true
		};  //excel download 옵션
		grdAproDtlList.advancedExcelDownload( excelOpt );
	};

	//목록버튼 클릭 이벤트
	scwin.btnList_onclick = function() {
       	var sData = {
    					 "paramKeySiteCd" : $w.getParameter("paramKeySiteCd")	//현장코드
    					,"paramKeySiteNm" :  $w.getParameter("paramKeySiteNm")	//현장명
    					,"paramKeyExeYy" :  $w.getParameter("paramKeyExeYy")	//기준년도
    					,"paramKeyDirmgExctAproCd" :  $w.getParameter("paramKeyDirmgExctAproCd")	//진행상태
    					,"paramSchSiteNm" :  $w.getParameter("paramSchSiteNm")	//검색조건 현장
    					,"paramKeyListBack" : "Y"	//목록화면 이동 FLAG
     				};
       	com.detailOpen("현장관리비예산승인조회", "CM15151005U", "/ux/cm/cm1515/CM15151000U.xml", "1", sData);
	};

   /**************************************************************************
	 * Event: scwin.aprvPublicAction
	 * 기 능: 결재처리후 결재번호 받아서 처리할 자체 업무이벤트 등록
	 **************************************************************************/
    scwin.aprvPublicAction = function() {
		dsTcmSiteMngctChgMap.set( "pbsvTeamAprvNo" , com.getAprvNo() );//공무_팀_결재번호
		dsTcmSiteMngctChgMap.set( "pbsvTeamAprvOpinCn" , importAprvMain.getWindow().dsAprvMap.get("aprvComment") );//공무_팀_결재_의견_내용
		dsTcmSiteMngctChgMap.set( "pbsvTeamAflId", importAprvMain.getWindow().dsAprvMap.get("aflId") );//공무_팀_첨부파일ID

        ajaxLib.ajax("/cm/cm1515/CM15150000U/updatEpbsvTeamAprvNo", {
            method : "POST",
            requestData : dsTcmSiteMngctChgMap.getJSON(),
            callback : //"fnCallback",
                function(result, e){
                	scwin.fnInitial();
            	},
            errCallback : "fnErrCallBack"
        });
    };
