		dsTcmSiteMngctChgMap.set( "pbsvTeamAflId", importAprvMain.getWindow().dsAprvMap.get("aflId") );//공무_팀_첨부파일ID

        ajaxLib.ajax("/cm/cm1515/CM15150000U/updatEpbsvTeamAprvNo", {
            method : "POST",
            requestData : dsTcmSiteMngctChgMap.getJSON(),
            callback : //"fnCallback",
                function(result, e){
                	scwin.fnInitial();
            	},
            errCallback : "fnErrCallBack"
        });
    };
