     /************************************************************************************
        DataCollection description

        dsStdYyList: 조회조건 기준년도
        dsDgrList: 조회조건 차수
        dsTcmSiteMngctChgDtlList: 현장관리비 내역 GRID
        dsSearchMap: 검색조건 parameter
        dsTcmSiteMngctChgMap: 현장_관리비_변경 정보
     ************************************************************************************/

	 /************************************************************************************
	 	결재상태

		CM00401301	[미요청]		현장_결재상신
		CM00401302	[요청]			현장_결재승인, 공무팀_기안취소
		CM00401303	[공무팀결재중]	공무팀_결재상신
		CM00401304	[완료]			공무팀_결재승인
		CM00401305	[공무팀_반송]
		CM00401306	[공무팀_부결]
     ************************************************************************************/

    /************************************************************************************
    *  [1] 전역변수
    ************************************************************************************/

    /************************************************************************************
    *  [2] onpageload(초기화포함)
    ************************************************************************************/
	scwin.onpageload = function() {
	    //추가 코드값 맵핑 호출
        scwin.fnInitial();

        //틀고정
        grdTcmSiteMngctChgDtlList.setFixedColumn( 10 );

		dsSearchMap.setJSON({
            "siteCd": $w.getParameter("paramKeySiteCd"),    //현장코드
            "exeYy": $w.getParameter("paramKeyExeYy"),      //기준년도
            "chgDgr": $w.getParameter("paramKeyChgDgr")
            });

        spnSiteNm.setValue( $w.getParameter("paramKeySiteNm") );//현장명
        spnExeYy.setValue( $w.getParameter("paramKeyExeYy") );//기준년도

        //차수조회
        scwin.fnDgrList();
	};

	scwin.onpageunload = function() {

	};

    /** 화면초기화 **/
    scwin.fnInitial = function() {
		grdTcmSiteMngctChgDtlList.initGrid();
        //UI팀용 함수 호출
        com.commonUI();
    };

    /************************************************************************************
     *  [3] 사용자 정의함수 함수
     ************************************************************************************/
    //차수조회
    scwin.fnDgrList = function() {
        ajaxLib.ajax("/cm/cm1515/CM15152000U/selectDgrList", {
            method : "POST",
            requestData : dsSearchMap.getJSON(),
            callback : //"fnCallback",
                function(result, e){
                	dsDgrList.removeAll();
	                dsDgrList.setJSON(result.data.dsDgrList);//차수

	                //차수 default로 최종 차수를 선택한다.
	                sbxChgDgr.setValue( dsDgrList.getCellData( dsDgrList.getTotalRow()-1 , "cd" ) );

	                //조회
	                scwin.fnSearch();
            	},
            errCallback : "fnErrCallBack"
        });
    }

	//조회
    scwin.fnSearch = function() {
        ajaxLib.ajax("/cm/cm1515/CM15152000U/selectHistList", {
            method : "POST",
            requestData : {
            				  param : dsSearchMap.getJSON()
            				 ,tcmSiteMngctChg : dsSearchMap.getJSON()
            			},
            callback : //"fnCallback",
                function(result, e){
                	dsTcmSiteMngctChgMap.setEmptyValue();
                	dsTcmSiteMngctChgDtlList.removeAll();

	                dsTcmSiteMngctChgMap.setJSON(result.data.dsTcmSiteMngctChgMap);//현장_관리비_변경 정보조회
	                dsTcmSiteMngctChgDtlList.setJSON(result.data.dsTcmSiteMngctChgDtlList);//현장관리비 내역 GRID LIST
	                com.setUdcValue("dsTotalCount","udcListCount",dsTcmSiteMngctChgDtlList.getTotalRow());   //GRID ROW COUNT

	               	grdTcmSiteMngctChgDtlList.setHeaderValue( "column289" , $w.getParameter("paramKeyExeYy") + "년도" );//헤더 TITLE변경
	               	var bfYear = parseInt($w.getParameter("paramKeyExeYy"), 10)-1;//전년도
	               	grdTcmSiteMngctChgDtlList.setHeaderValue( "column290" , "전년 누계실적<br/>(" + bfYear + ")" );//헤더 TITLE변경
            	},
            errCallback : "fnErrCallBack"
        });
    }

	//Font 색상 변경
	scwin.fnFontColor = function(value, formattedValue , rowIdx, colIdx) {
    	if(parseInt(value, 0) < 0) {
    		grdTcmSiteMngctChgDtlList.setCellColor( rowIdx , colIdx , "red" );//음수
    	} else {
    		grdTcmSiteMngctChgDtlList.setCellColor( rowIdx , colIdx , "black" );//양수
    	}
    	return formattedValue;
	};

    /************************************************************************************
     *  [4] Callback 함수
     ************************************************************************************/
    /** 오류시 처리 **/
    scwin.fnErrCallBack = function(result, e) {

    };

    /************************************************************************************
     *  [5] Event 함수
     ************************************************************************************/
	//조회 클릭 이벤트
	scwin.btnFind_onclick = function() {
		if(sbxChgDgr.getValue() == null || sbxChgDgr.getValue() == ""){
			com.showMessage("CFW0004", "차수", "", "", sbxChgDgr.id);//$1을(를) 선택하십시오.
			return;
		}else{
			scwin.fnSearch();
		}
	};

	//결재현황 클릭 이벤트
	scwin.btnSituation_onclick = function() {
		var popOps = {
						  popup_name:"결재현황"
			           	, url:"/ux/cm/cm1515/CM15150005P.xml"
			           	, data:{
									 paramKeySiteCd : dsTcmSiteMngctChgMap.get( "siteCd" )//현장코드
									,paramKeyExeYy : dsTcmSiteMngctChgMap.get( "exeYy" )//실행_년도
									,paramKeyChgDgr : dsTcmSiteMngctChgMap.get( "chgDgr" )//변경_차수
			           	       }
			           	, callbackFn : ""
			           	, widType:"XL"
			           	, height:"750"
		             };
		com.popup_open(popOps);
	};

	//엑셀 다운로드 이벤트
	scwin.btnExcelDwn_onclick = function() {
		var result = [];
		var totalCol = grdTcmSiteMngctChgDtlList.getTotalCol();
		for(var i = 0; i < totalCol; i++) {
			if (!grdTcmSiteMngctChgDtlList.getColumnVisible(i)) {
				result.push(i);
			}
		}
		var excelOpt = {
			 fileName: "현장관리비이력조회(내역).xlsx"    //또는 xlsx 확장자 사용
			,removeColumns: result.join(",")
		    ,colMerge: true    				//colMerge된 컬럼을 Merge해서 출력 할 지 여부
		    ,colMergeTextAlign: "center"    //colMerge된 컬럼의 textAlign설정 (bottom, center, top 설정)
            ,useSubTotal : true             // Sub-Total 표시 
            ,headerFontColor : "#FFFFFF"    // Header Font Color
            ,headerColor : "#D9D9D9"        // Header Back Color
            ,subTotalColor : "#FCE4D6"      // SubTotal Back Color
            ,footerColor : "#BDD7EE"        // Footer Color
		};  //excel download 옵션
		grdTcmSiteMngctChgDtlList.advancedExcelDownload( excelOpt );
	};

	//목록버튼 클릭 이벤트
	scwin.btnList_onclick = function() {
       	var sData = {
    					 "paramKeySiteCd" : $w.getParameter("paramKeySiteCd")	//현장코드
    					,"paramKeySiteNm" :  $w.getParameter("paramKeySiteNm")	//현장명
    					,"paramKeyExeYy" :  $w.getParameter("paramKeyExeYy")	//기준년도
    					,"paramKeyListBack" : "Y"	//목록화면 이동 FLAG
     				};
       	com.detailOpen("현장관리비이력조회", "CM15152000U", "/ux/cm/cm1515/CM15152005U.xml", "1", sData);
	};
