    /************************************************************************************
     *  [1] onpageload
     ************************************************************************************/
    scwin.onpageload = function() {

		//화면초기화
        scwin.fnInitial();

    };

    /** 화면초기화 **/
    scwin.fnInitial = function() {
    
    	//소계 배경색 설정
        grdTcmLbatnPfmcList.setColumnBackgroundColor("baseTmSum", "#f0e9f4");	//기본 소계
        grdTcmLbatnPfmcList.setColumnBackgroundColor("extnTmSum", "#f0e9f4");	//연장 소계
        grdTcmLbatnPfmcList.setColumnBackgroundColor("yyvctCnt", "#f0e9f4");	//연차 소계

		var sFrameId = "";
		if ( gcm.tab_main == undefined  ) {		//팝업인경우 
			var winPopParam = JSON.parse(sessionStorage.winpopParam);
			var parentId    = winPopParam.dataObj.data.pframeId;
			
			if(parentId != undefined && parentId != "") {
           		var parentObj = opener.frames[parentId].getWindow().com;
           		
		        //원천세 여부
           		//cmn_CM.js 에 있는 함수 호출 
           		if (typeof(parentObj["getLaborGbn"]) == "function"){
           			sLaborGbn = parentObj["getLaborGbn"](parentId,"MNU_LV_307040");
           		}else{
           			sLaborGbn   = cmCom.getLaborGbn(parentId, "MNU_LV_307040");
           		}
           		
           		if (typeof(parentObj["getSiteDeptNm"]) == "function"){
           			sSiteDeptNm = parentObj["getSiteDeptNm"](sLaborGbn);
           		}else{
           			sSiteDeptNm = cmCom.getSiteDeptNm(sLaborGbn);
           		}
           		
           		if (typeof(parentObj["getLaborGbn"]) == "function"){
           			objSiteDept = parentObj["setSiteOrDept"](sLaborGbn, wfmKeySiteInfo, wfmKeyDeptInfo, spnSiteDept);
           		}else{
           			objSiteDept = cmCom.setSiteOrDept(sLaborGbn, wfmKeySiteInfo, wfmKeyDeptInfo, spnSiteDept);
           		}
			}
		} else {								//팝업이 아닌경우
			sFrameId = com.getFrameId() ;
			
	        //원천세 여부
			sLaborGbn   = cmCom.getLaborGbn(sFrameId, "MNU_LV_307040");
	        sSiteDeptNm = cmCom.getSiteDeptNm(sLaborGbn);
	        objSiteDept = cmCom.setSiteOrDept(sLaborGbn, wfmKeySiteInfo, wfmKeyDeptInfo, spnSiteDept);
		}
		
		
        dsSrchMap.set("laborGbn", sLaborGbn);   //원천세 구분

        //초기값 설정
        com.setUdcValue("udcLbatnYymm", "calYearMonth", $p.getCurrentServerDate( "yyyyMM" ));  	//출연년월
        dsSrchMap.set("keyLbatnYymm",     com.getUdcValue("udcLbatnYymm", "calYearMonth"));   	//출연년월

		com.selCdList( "grdTcmLbatnPfmcList:erndGbCd", "CM005001", "Y", "Y");   				//근로구분코드
		com.selCdList( "keyErndGbCd", "CM005001", "Y", "Y");  //근로구분코드

        scwin.fnSelectInit();	//초기 조회
        
        //UI팀용 함수 호출
        scwin.commonUI();
    };

    /************************************************************************************
     *  [2] 전역변수
     ************************************************************************************/
    var svcId = "";
    var sLaborGbn = "";
    var sSiteDeptNm = "현장";
    var objSiteDept = null;

    /************************************************************************************
     *  [3] Event 함수
     ************************************************************************************/

    // 검색 버튼
    scwin.btnFind_onclick = function() {
        scwin.fnSearchList();
    };

    // 그리드 저장
    scwin.btnSave_onclick = function() {
        scwin.fnUpdate();
    };

    // 그리드 변경 이벤트 ( 그리드 변경시 Temp DataSet에 추가한다)
    scwin.grdTcmLbatnPfmcList_onafteredit = function(row,col,value) {
        
    };

	scwin.grdTcmLbatnPfmcList_onviewchange = function(info) {

		var col = info.colIndex;
		var row = info.rowIndex;
		var value = info.newValue;

        var sColumnId =  grdTcmLbatnPfmcList.getColumnID( col );    //수정 컬럼 ID

        if( sColumnId.indexOf("baseTm") >= 0){      //기본
            if( value > 8){                         //기본 8시간 초과시 0으로 세팅
                value = "";
                com.showMessage("CMW0040", "", "", "", grdTcmLbatnPfmcList.id, row, sColumnId);	//기본시간은8시간을 초과할수 없습니다.
            }
        }

        if( sColumnId.indexOf("extnTm") >= 0){      //연장
            if( value > 4){                         //연장 4시간 초과시 0으로 세팅
                value = "";
                com.showMessage("CMW0041", "", "", "", grdTcmLbatnPfmcList.id, row, sColumnId);	//연장시간은 4시간을 초과할수 없습니다.
            }
        }

		if( sColumnId != "baseTmAll"){
        	scwin.fnEdit(row,col,value);
        }
        
        if( sColumnId == "baseTmAll"){
        	var value =  0;
        	
        	//모든 값을 0으로 세팅
        	for( var i = 0 ; i < dsTcmLbatnPfmcDateList.getRowCount() ; i++){
        		var nColIndex = cmCom.parseInt(grdTcmLbatnPfmcList.getColumnIndex( "baseTm"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) ));

                var nWeek      = dsTcmLbatnPfmcDateList.getCellData( i , "selectWeek" ); 	//요일값
                var sHolidayYn = dsTcmLbatnPfmcDateList.getCellData( i , "holidayYn" ); 	//휴일여부

				if( !grdTcmLbatnPfmcList.getCellReadOnly( row , nColIndex ) &&    !(nWeek == 1 || sHolidayYn == "Y")  ) {
       				dsTcmLbatnPfmcList.setCellData( row , "baseTm"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  , 0);		//기본시간 세팅
       				dsTcmLbatnPfmcList.setCellData( row , "extnTm"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  , 0);		//연장시간 세팅
        			scwin.fnEdit(row,nColIndex,0);
       			}
        	
        	}
        	
        	//공휴일, 토요일, 일요일을 제외한 근무일자로 세팅
	        if( dsTcmLbatnPfmcList.getCellData( row , 'baseTmAll') == "Y" ){
	        	value =  cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'dyDyWkTm')) ;	//기본시간
	        	var nExtnTm = 0;																//연장시간
	        	if( eval(value) > 8 ){
	        		nExtnTm = value - 8;  		//연장 시간 = 기본시간 - 8
	        		value = 8;					//기본시간
	        	} 
	        	
	        	
	        	for( var i = 0 ; i < dsTcmLbatnPfmcDateList.getRowCount() ; i++){
	        		var nColIndex = cmCom.parseInt(grdTcmLbatnPfmcList.getColumnIndex( "baseTm"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) ));
	
	                var nWeek      = dsTcmLbatnPfmcDateList.getCellData( i , "selectWeek" ); 	//요일값
	                var sHolidayYn = dsTcmLbatnPfmcDateList.getCellData( i , "holidayYn" ); 	//휴일여부
	
	                if( !grdTcmLbatnPfmcList.getCellReadOnly( row , nColIndex ) &&  !(nWeek == 1 || nWeek == 7  || sHolidayYn == "Y") ){		// 1 : 일요일 , 7 : 토요일 , Y : 공휴일
	        			dsTcmLbatnPfmcList.setCellData( row , "baseTm"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  , value);			//기본시간 세팅
	        			dsTcmLbatnPfmcList.setCellData( row , "extnTm"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  , nExtnTm);		//연장시간 세팅
	        			scwin.fnEdit(row,nColIndex,value);
	       			}  
	        	}
	       	}
        }  
        
	};

    // 그리드 체크박스(연차) 이벤트
    scwin.grdTcmLbatnPfmcList_oncellclick = function(row,col) {
       var sColumnId =  grdTcmLbatnPfmcList.getColumnID( col );    //수정 컬럼 ID
       if( sColumnId.indexOf("Yn")  >= 0  ){
           if( grdTcmLbatnPfmcList.getCellChecked( row , col )){
               scwin.fnEdit(row,col,"Y");
           }else{
               scwin.fnEdit(row,col,"N");
           }
       	}
    };

    //검색조건 성명
    scwin.keyWkrNm_onkeydown = function(e) {
        if (e.keyCode == 13){
            scwin.fnSearchList();
        }
    };

    //엑셀다운 버튼
    scwin.btnExcelDwn_onclick = function() {
        if(grdTcmLbatnPfmcList.getTotalRow() <= 0){
            com.showMessage("CMW0042");     //다운로드할 내역이 존재하지 않습니다.
            return;
        }else{
            var excelOpt = {};  //excel download 옵션
            excelOpt.fileName = "월별출역관리.xlsx";    //또는 xlsx 확장자 사용
            grdTcmLbatnPfmcList.advancedExcelDownload( excelOpt );
        }
    };

   /**************************************************************************
     * 함 수 명: scwin.WFrameSiteInfo_change_callback
     * 기 능: 현장코드 변경 이벤트
     **************************************************************************/    
	scwin.WFrameSiteInfo_change_callback = function() {
        dsTcmLbatnPfmcList.removeAll();
	};  
	
    /**************************************************************************
     *  [4] Callback
     **************************************************************************/

    // 결과 후처리
    scwin.fnCallback = function(result, e) {

        switch (svcId) {
            case "selectInitTcmLbatnPfmc" :
                dsTcmLbatnPfmcDateList.setJSON(result.data.tcmLbatnPfmcDateList);       //검색 년월 일자
                dsTcmLbatnPfmcCodeMap.setJSON(result.data.tcmLbatnPfmcCodeMap);         //근무가능시간 공통코드 조회

                scwin.fnGridHeader();   //그리드 헤더 적용

                break;
            case "selectTcmLbatnPfmcList" :

                dsTcmLbatnPfmcList.setJSON(result.data.tcmLbatnPfmcList);               //그리드 검색 결과
                dsTcmLbatnPfmcDateList.setJSON(result.data.tcmLbatnPfmcDateList);       //검색 년월 일자
				var sCloseYn = result.data.closeYn;										//마감여부
				var sSlipYn  = result.data.slipYn;										//전표여부
				var sAprvYn  = result.data.aprvYn;										//결제여부

				if( sCloseYn== "Y" ) { 
					tbxCloseDisp.setValue( "해당월의 노무비가 마감되었습니다." );
				} else if( sSlipYn== "Y" ) { 
					tbxCloseDisp.setValue( "해당월의 전표중이거나 마감되었습니다." );
				} else if( sAprvYn== "Y" ) { 
					tbxCloseDisp.setValue( "해당월의 결제중이거나 마감되었습니다." );
				}

                dsTcmLbatnPfmcTempList.setJSON( [] );                                   //저장할 DATA SET 리셋

                scwin.fnGridHeader();    												//그리드 헤더 적용

				// 각각의 근로자 기준으로 기본, 연장, 연장 ReadOnly 설정 
		        var nRowCnt = dsTcmLbatnPfmcDateList.getRowCount();

		        for( var nRow = 0 ; nRow < dsTcmLbatnPfmcList.getRowCount() ; nRow++){
		            var nWeek = 0;
		            var sHolidayYn = "";
		            for( var i = 0 ; i < nRowCnt ; i++ ){
		                var sEditYn = dsTcmLbatnPfmcList.getCellData( nRow , "editYn"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  );	//등록 가능 여부

		                nWeek      = dsTcmLbatnPfmcDateList.getCellData( i , "selectWeek" ); 	//요일값
                        sHolidayYn = dsTcmLbatnPfmcDateList.getCellData( i , "holidayYn" ); 	//휴일여부

                        if( !(nWeek == 1 || sHolidayYn == "Y") ){		// 1 : 일요일 , Y : 공휴일
			                if( sEditYn == "Y"   ){    					// 수정여부(근로 기간인 경우)  
			                    grdTcmLbatnPfmcList.setCellReadOnly( nRow , "baseTm"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , false );	//기본
			                    grdTcmLbatnPfmcList.setCellReadOnly( nRow , "extnTm"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , false );	//연장
			                    grdTcmLbatnPfmcList.setCellReadOnly( nRow , "yyvctYn"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , false );	//연차
			                } else {
			                    grdTcmLbatnPfmcList.setCellReadOnly( nRow , "baseTm"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , true );		//기본
	                            grdTcmLbatnPfmcList.setCellReadOnly( nRow , "extnTm"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , true );		//연장
	                            grdTcmLbatnPfmcList.setCellReadOnly( nRow , "yyvctYn"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , true );	//연차
			                }
		                } else {										// 일요일이거나 공휴일 인경우 연차항목만 등록 가능
		                	if( sEditYn == "Y"   ){    					// 수정여부(근로 기간인 경우)
		                		grdTcmLbatnPfmcList.setCellReadOnly( nRow , "extnTm"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , false );
		                	}
		                }
						//급여 계산을 하였거나, 해당 월 노무 마감인 경우 수정 불가능
		                if( eval(dsTcmLbatnPfmcList.getCellData( nRow ,"stateNo")) >= 2 || sCloseYn == 'Y' /* || sSlipYn == 'Y' || sAprvYn == 'Y' 2020.04.03 마감 or 세금계산이면 read only (정지윤) */ ){   //정산한 노무자의 출역은 삭제할 수 없습니다. 정산을 먼저 해제하세요.
		                    grdTcmLbatnPfmcList.setCellReadOnly( nRow , "baseTm"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , true );     //기본 시간
                            grdTcmLbatnPfmcList.setCellReadOnly( nRow , "extnTm"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , true );     //연장 시간
                            grdTcmLbatnPfmcList.setCellReadOnly( nRow , "yyvctYn"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , true );    //연차
                            grdTcmLbatnPfmcList.setCellReadOnly( nRow , "rmkCn" , true );                                                           //비고
                            grdTcmLbatnPfmcList.setCellReadOnly( nRow , "baseTmAll", true );    //출역
                            
		                }
		            }
		        }

                break;
            case "saveTcmLbatnPfmc" :
                com.showMessage("CFA0012");      //저장되었습니다.
                scwin.fnSearchList();
                break;
        }
    };

    // 에러 후처리
    scwin.fnErrCallBack = function(e) {
        switch (svcId) {
            case "selectInitTcmLbatnPfmc" :
                console.log('조회오류: ' + e);
                break;
            case "selectTcmLbatnPfmcList" :
                console.log('조회오류: ' + e);
                break;
            case "saveTcmLbatnPfmc" :
                console.log('저장오류: ' + e);
                break;
        }
    }

    /**************************************************************************
     *  [5] 사용자정의 함수
     **************************************************************************/
    // 조회 List
    scwin.fnSelectInit = function() {

        svcId = "selectInitTcmLbatnPfmc";

        ajaxLib.ajax("/cm/cm4005/CM40050000U/selectInitTcmLbatnPfmc", {
            mode : "synchronous",
            method : "POST",
            requestData : dsSrchMap.getJSON(),
            callback : "fnCallback",
            errCallback : "fnErrCallBack"
        });

    };

    // 조회 List
    scwin.fnSearchList = function() {

        //DS 매핑
		if( dsSrchMap.get("laborGbn") == "TX") {
        	dsSrchMap.set("keySiteCd"   , wfmKeyDeptInfo.getWindow().ibxKeySiteCd.getValue() );  	//부서 코드
        }else{
	        dsSrchMap.set("keySiteCd"   , wfmKeySiteInfo.getWindow().ibxKeySiteCd.getValue() );  	//현장 코드
        }
        dsSrchMap.set("keyLbatnYymm",     com.getUdcValue("udcLbatnYymm", "calYearMonth"));   		//출연년월

        if( dsSrchMap.get("keySiteCd").length == 0  ){												//현장/부서
            if( dsSrchMap.get("laborGbn") == "TX") {
	            var focusId = wfmKeyDeptInfo.getWindow().ibxKeySiteNm.id;
	            com.showMessage("CMW0025", "부서", "", "", focusId);     							//$1은(는) 필수입력사항입니다.
            }else{
	            var focusId = wfmKeySiteInfo.getWindow().ibxKeySiteNm.id;
	            com.showMessage("CMW0025", "현장", "", "", focusId);     							//$1은(는) 필수입력사항입니다.
            }
            return ;
        }

        if( dsSrchMap.get("keyLbatnYymm").length == 0  ){											//출역일자
            var focusId = com.getUdcComp("udcLbatnYymm", "calYearMonth").id;
            com.showMessage("CMW0025", "출연년월", "", "", focusId); 								//$1은(는) 필수입력사항입니다.
            return ;
        }

        svcId = "selectTcmLbatnPfmcList";

        ajaxLib.ajax("/cm/cm4005/CM40050000U/selectTcmLbatnPfmcList", {
            mode : "asynchronous",
            method : "POST",
            requestData : dsSrchMap.getJSON(),
            callback : "fnCallback",
            errCallback : "fnErrCallBack"
        });

    };

    // 저장
    scwin.fnUpdate = function() {

        if( dsTcmLbatnPfmcList.getModifiedJSON().length == 0 ){
            com.showMessage("CMW0024"); //저장(변경)할 내용이 없습니다.
            return ;
        }

        for( var i = 0 ; i < dsTcmLbatnPfmcTempList.getRowCount() ; i++){           				//Temp DataSet 등록/수정 항목
            var nRow = dsTcmLbatnPfmcTempList.getCellData( i , "row" );
            var nCol = dsTcmLbatnPfmcTempList.getCellData( i , "col" );

            if( eval( dsTcmLbatnPfmcTempList.getCellData( i , "extnTm" ) ) > 8 ){   				//기본 8시간 초과시
                com.showMessage("CMW0040", "", "", "", grdTcmLbatnPfmcList.id, nRow, nCol);			//기본시간은8시간을 초과할수 없습니다.
                return ;
            }

            if( eval( dsTcmLbatnPfmcTempList.getCellData( i , "extnTm" ) ) > 4 ){   				//연장 4시간 초과시
                value = "";
                com.showMessage("CMW0041", "", "", "", grdTcmLbatnPfmcList.id, nRow, nCol);			//연장시간은 4시간을 초과할수 없습니다.
                return ;
            }

            //기본 출역 등록 여부 확인
            if( eval( dsTcmLbatnPfmcTempList.getCellData( i , "baseTm" ) ) == 0     				//기본
             && eval( dsTcmLbatnPfmcTempList.getCellData( i , "extnTm" ) ) > 0){    				//연장
				
				if( "Y" != dsTcmLbatnPfmcTempList.getCellData( i , "holidayYn" ) 
				&&  "1" != dsTcmLbatnPfmcTempList.getCellData( i , "wkdGbCd" )   ){					//공휴일, 일요일은 연장만 등록 가능
					com.showMessage("CMW0043", "", "", "", grdTcmLbatnPfmcList.id, nRow, nCol);		//기본출역시간이 입력되지 않았습니다.
		            return ;
				} 
            }

			//비고만 등록 할경우 저장되지 않음, 1건 이상의 출역등록 여부 체크
            var bRmkCnChk = false;
            if( dsTcmLbatnPfmcTempList.getCellData( i , "lbatnDt").length == 6  					//비고인경우
             && dsTcmLbatnPfmcTempList.getCellData( i , "rmkCn").length > 0  ){						//비고에 값이 잇을 경우
            	for( var k = 0 ; k < dsTcmLbatnPfmcDateList.getRowCount() ; k++){
            		var nBaseTm = dsTcmLbatnPfmcList.getCellData( nRow , "baseTm"+dsTcmLbatnPfmcDateList.getCellData( k , "selectDd" ));	//기본
            		var nExtnTm = dsTcmLbatnPfmcList.getCellData( nRow , "extnTm"+dsTcmLbatnPfmcDateList.getCellData( k , "selectDd" ));	//연장

            		if( nBaseTm > 0 ||  nExtnTm > 0){
            			bRmkCnChk = true;
            		}
            	}

            	if( bRmkCnChk == false ){
            		com.showMessage("CMW0127", "", "", "", grdTcmLbatnPfmcList.id, i,  "rmkCn");	//비고를 등록을 위해서는 출역을 등록을 해야 합니다.
            		return ;
            	}

            }
        }

        svcId = "saveTcmLbatnPfmc";

        if (confirm("저장 하시겠습니까?")) {

            ajaxLib.ajax("/cm/cm4005/CM40050000U/saveTcmLbatnPfmc", {
                mode : "asynchronous",
                method : "POST",
                requestData : {
                                param : dsSrchMap.getJSON()
                             , tcmLbatnPfmcList : dsTcmLbatnPfmcTempList.getModifiedJSON()
                              },
                callback : "fnCallback",
                errCallback : "fnErrCallBack"
            });
        }
    };

    // 그리드 헤더 세팅
    scwin.fnGridHeader = function() {

		//조회 후 그리드 해당 월 마지막 일자까지만 표시
		//토요일, 일요일, 공휴일 그리드 헤더에 색표시 (토요일 파랑색, 일요일/공휴일 빨강색) 
		
        // 컬럼 일자 기준으로 컬럼 생성
        var nRowCnt = dsTcmLbatnPfmcDateList.getRowCount();

        grdTcmLbatnPfmcList.setColumnVisible( "baseTm29", false );		//29일
        grdTcmLbatnPfmcList.setColumnVisible( "baseTm30", false );		//30일
        grdTcmLbatnPfmcList.setColumnVisible( "baseTm31", false );		//31일

        var nWeek = 0;
        var sHolidayYn = "N";
        for( var i = 0 ; i < nRowCnt ; i++ ){
            nWeek = dsTcmLbatnPfmcDateList.getCellData( i , "selectWeek" ); 		//요일값
            sHolidayYn = dsTcmLbatnPfmcDateList.getCellData( i , "holidayYn" ); 	//요일값 1 : 일요일

            grdTcmLbatnPfmcList.removeHeaderClass( "colDay"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , "fc_red_bold"  );    //적용 클래스 제거
            grdTcmLbatnPfmcList.removeHeaderClass( "colDay"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , "fc_blue_bold"  );   //적용 클래스 제거
            grdTcmLbatnPfmcList.setColumnReadOnly( "baseTm"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , false );             //기본 readonly 품
            grdTcmLbatnPfmcList.setColumnReadOnly( "extnTm"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , false );             //연장 readonly 품

            grdTcmLbatnPfmcList.setColumnReadOnly( "yyvctYn"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , false );            //연장 readonly 품

            if( nWeek == 1 || sHolidayYn == "Y" ){        //일요일, 공휴일
                grdTcmLbatnPfmcList.setHeaderClass( "colDay"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , "fc_red_bold" );    //일요일 클래스 적용
                grdTcmLbatnPfmcList.setColumnReadOnly( "baseTm"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , true );          //기본 readonly 적용
                grdTcmLbatnPfmcList.setColumnReadOnly( "extnTm"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , true );          //연장 readonly 적용
                grdTcmLbatnPfmcList.setColumnReadOnly( "yyvctYn"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , true );         //연장 readonly 품

            }else if( nWeek == 7){  //토요일
                grdTcmLbatnPfmcList.setHeaderClass( "colDay"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , "fc_blue_bold" );   //토요일 클래스 적용
            }

            // 29일 이후 부터 해당 월 마지막 날까지 보여지도록
            if( i >= 28 ){       //29일, 30일, 31일
                grdTcmLbatnPfmcList.setColumnVisible( "baseTm"+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" ) , true );           //해당 요일 보여줌
            }
        }

    }

    // 그리드 수정시 발생하는 이벤트
    //  1. 소계 계산
    //  2. Temp DataSet 으로 등록
    scwin.fnEdit = function(row,col,value) {
    
       var sColumnId =  grdTcmLbatnPfmcList.getColumnID( col );    						//수정 컬럼 ID
       var selectDd = '';

       var sErndSeq = dsTcmLbatnPfmcList.getCellData( row , 'erndSeq');            		//근로_순번
       var sWkrNo   = dsTcmLbatnPfmcList.getCellData( row , 'wkrNo');              		//근로자번호
       var sSiteCd  = dsTcmLbatnPfmcList.getCellData( row , 'siteCd');             		//현장코드

       var sLbatnDt   = "";       	//출역_일자
       var sBaseTm    = 0;       	//기본_시간
       var sExtnTm    = 0;       	//연장_시간
       var sRmkCn     = "";       	//비고
       var sHorwgAmt  = "";       	//시급
       var sWkdGbCd   = "";       	//요일구분
       var sYyvctYn   = "";       	//연차여부
       var sHolidayYn = '';			//휴일여부

       if( sColumnId.indexOf("Tm")  >= 0 || sColumnId.indexOf("Yn")  >= 0  ){           //기본, 연정, 연차 항목 여부,
           //******** 기본/연차 값 validation **********
           if( sColumnId.indexOf("Tm")  >= 0 ){     //기본, 연차 항목
               selectDd = sColumnId.substring(sColumnId.indexOf("Tm")+2);
               if( value == ""){   														//등록값이 없을 경우 0.0으로 세팅
                   value = 0.0;
                   dsTcmLbatnPfmcList.setCellData( row , sColumnId  , value);
               }
           }
           
           //연차인 경우 기본값 8시간으로 세팅
           if( sColumnId.indexOf("Yn")  >= 0 ){   										//연차 항목
              selectDd = sColumnId.substring(sColumnId.indexOf("Yn")+2);
              if( value == "Y"){   														//연차를 체크 한경우
                  dsTcmLbatnPfmcList.setCellData( row , 'baseTm'+selectDd  , 8 );     	//기본 값 세팅
                  dsTcmLbatnPfmcList.setCellData( row , 'extnTm'+selectDd  , 0 );     	//연장 값 세팅
              }else{

                  dsTcmLbatnPfmcList.setCellData( row , 'baseTm'+selectDd  , 0 );     	//기본 값 세팅
                  dsTcmLbatnPfmcList.setCellData( row , 'extnTm'+selectDd  , 0 );     	//연장 값 세팅
              }
           }

		  //******** 기본/연차 초과 근무 시간 validation **********
		  // 1,2번째주 합, 3,4번째 주 합, 5,6번째 주 합을 구한다.
		  // 1번째는 전달에 있을 경우 전달 합에 더한다. 
           var nWeekNo = dsTcmLbatnPfmcList.getCellData( row , 'weekNo'+selectDd);  //주간 번호 1: 1번째주, 2: 2번째주, 3: 3번째주....
           var nWeekBaseTmSum = 0;      //기본 값 주간 합
           var nWeekExtnTmSum = 0;      //연장 값 주간 합
           var n2WeekBaseTmSum = 0;     //기본 값 2주간 합
           var n2WeekExtnTmSum = 0;     //연장 값 2주간 합

           if( nWeekNo == 1 ){      	//1번째 주 인경우 전달 합을 가져옴.
               nWeekBaseTmSum =  cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'beforeTotBaseTm'),0);    //이전 달 기본값 합
               nWeekExtnTmSum =  cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'beforeTotExtnTm'),0);    //이전 달 연장값 합
               n2WeekBaseTmSum = nWeekBaseTmSum;
               n2WeekExtnTmSum = nWeekExtnTmSum;
           }

		   //2주 간격으로 합 구하기 
           for( var i = 0 ; i < dsTcmLbatnPfmcDateList.getRowCount() ; i++){
               //수정한 일자의 주 합
               if(nWeekNo == cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'weekNo'+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  ), 0) ){
                    nWeekBaseTmSum = nWeekBaseTmSum + cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'baseTm'+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  ),0.0);
                    nWeekExtnTmSum = nWeekExtnTmSum + cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'extnTm'+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  ),0.0);
               }

               //수정한 일자의 2주의 합
               if( nWeekNo == 1 || nWeekNo == 2){           //1,2번재 주
                    if( 1 == cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'weekNo'+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  ), 0)
                     || 2 == cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'weekNo'+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  ), 0) ){
	                    n2WeekBaseTmSum = n2WeekBaseTmSum + cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'baseTm'+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  ),0.0);
	                    n2WeekExtnTmSum = n2WeekExtnTmSum + cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'extnTm'+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  ),0.0);
                    }
               }else if( nWeekNo == 3 || nWeekNo == 4){     //3,4번째 주
                    if( 3 == cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'weekNo'+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  ), 0)
                     || 4 == cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'weekNo'+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  ), 0) ){
                        n2WeekBaseTmSum = n2WeekBaseTmSum + cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'baseTm'+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  ),0.0);
                        n2WeekExtnTmSum = n2WeekExtnTmSum + cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'extnTm'+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  ),0.0);
                    }
               }else if( nWeekNo == 5 || nWeekNo == 6){     //5,6번째 주(해당 항복 큰 의미 없음)
                    if( 5 == cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'weekNo'+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  ), 0)
                     || 6 == cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'weekNo'+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  ), 0) ){
	                    n2WeekBaseTmSum = n2WeekBaseTmSum + cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'baseTm'+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  ),0.0);
	                    n2WeekExtnTmSum = n2WeekExtnTmSum + cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'extnTm'+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  ),0.0);
                    }
               }
           }

           var nExtendOfWeek = eval(dsTcmLbatnPfmcCodeMap.get("extendOfWeek")); 									//12시간
           if( nWeekExtnTmSum > nExtendOfWeek ){																	//주간 연장 근무 시간 합 > 12
	            dsTcmLbatnPfmcList.setCellData( row , 'extnTm'+selectDd  , 0 );     								//연장 값 세팅
	            com.showMessage("CMW0044", nExtendOfWeek, "", "", grdTcmLbatnPfmcList.id, row, 'extnTm'+selectDd);	//연장 근무시간은 주 $1시간을 초과할수없습니다.
	            return ;
           }

           if( n2WeekBaseTmSum > 80){																				//2주 기본 근무 시간 합 > 80
                com.showMessage("CMW0045", "", "", "", grdTcmLbatnPfmcList.id, row, 'baseTm'+selectDd);				//기본 근무시간은 2주 80시간을 초과할수없습니다.
                dsTcmLbatnPfmcList.setCellData( row , 'baseTm'+selectDd  , 0 );             						//기본 값 세팅
                dsTcmLbatnPfmcList.setCellData( row , 'editYn'+selectDd  , "N" );           						//연차 값 세팅
                grdTcmLbatnPfmcList.setCellChecked( row , "yyvctYn"+selectDd  , false );    						//연차여부
                return ;
           }

           //******** 기본/연차 값 계산 **********

           var nBaseTmSum = 0;  //기본 소계
           var nExtnTmSum = 0;  //연장 소계
           var nYyvctYnSum = 0;  //연차 소계

           for( var i = 0 ; i < dsTcmLbatnPfmcDateList.getRowCount() ; i++){

               // 기본 소계 계산
               nBaseTmSum  = nBaseTmSum + cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'baseTm'+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  ),0.0);                                      //기본 소계 + 수정된 기본값

               // 연장 소계 계산
               nExtnTmSum  = nExtnTmSum + cmCom.parseInt(dsTcmLbatnPfmcList.getCellData( row , 'extnTm'+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  ),0.0);                                       //연장 소계 + 수정된 연장값

               if(selectDd == dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )){		//등록한 일자가 요일및 공유일 여부 찾기
                    sWkdGbCd = dsTcmLbatnPfmcDateList.getCellData( i , "selectWeek" );  	//선택 요일
                    sHolidayYn = dsTcmLbatnPfmcDateList.getCellData( i , "holidayYn" );  	//선택 휴일
               }

			   // 연차 소계 계산
               if( dsTcmLbatnPfmcList.getCellData( row , 'yyvctYn'+dsTcmLbatnPfmcDateList.getCellData( i , "selectDd" )  ) == "Y") {	
               		nYyvctYnSum ++;
               }

           }
           dsTcmLbatnPfmcList.setCellData( row , 'baseTmSum'  , nBaseTmSum );			//기본 소계
           dsTcmLbatnPfmcList.setCellData( row , 'extnTmSum'  , nExtnTmSum );			//연장 소계
           dsTcmLbatnPfmcList.setCellData( row , 'yyvctCnt'   , nYyvctYnSum );			//연차 소계 

           sLbatnDt  = dsTcmLbatnPfmcList.getCellData( row , 'lbatnDt'+selectDd);       //출역_일자
           sBaseTm   = dsTcmLbatnPfmcList.getCellData( row , 'baseTm'+selectDd);        //기본_시간
           sExtnTm   = dsTcmLbatnPfmcList.getCellData( row , 'extnTm'+selectDd);        //연장_시간
           sHorwgAmt = dsTcmLbatnPfmcList.getCellData( row , 'chgHorwgAmt'+selectDd);   //시급
           sYyvctYn  = dsTcmLbatnPfmcList.getCellData( row , 'yyvctYn'+selectDd);       //연차여부
       }else  if( sColumnId.indexOf("rmkCn")  >= 0 ){     //비고 항복 여부
           //******** 비고 세팅 **********
           sLbatnDt = dsTcmLbatnPfmcList.getCellData( row , 'lbatnDt');            		//출역_일자
           sRmkCn   = dsTcmLbatnPfmcList.getCellData( row , 'rmkCn');              		//비고
       }

       //******** Temp DataSet 등록 ********
       //Temp DataSet 에 데이터 근로자 일자가 존재 유무 확인
       var nRowIndex = -1;
       for( var i = 0 ; i < dsTcmLbatnPfmcTempList.getRowCount() ; i++){           		//Temp DataSet 존재 유무 확인

           // 키값 기준으로 존재할 경우 확인
           if( dsTcmLbatnPfmcTempList.getCellData( i , "lbatnDt") == sLbatnDt     		//출역_일자
            && dsTcmLbatnPfmcTempList.getCellData( i , "erndSeq") == sErndSeq     		//근로_순번
            && dsTcmLbatnPfmcTempList.getCellData( i , "wkrNo") == sWkrNo       		//근로자번호
            && dsTcmLbatnPfmcTempList.getCellData( i , "siteCd") == sSiteCd){     		//현장코드
              nRowIndex = i;
              break;
           }

       }
	   
       if( nRowIndex == -1){   // Temp DataSet에 값이 존재하 않을 경우 행 추가
           nRowIndex = dsTcmLbatnPfmcTempList.insertRow();
       }

       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "lbatnDt" 	, sLbatnDt );     	//출역_일자
       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "erndSeq" 	, sErndSeq );     	//근로_순번
       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "wkrNo"   	, sWkrNo );       	//근로자번호
       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "siteCd"  	, sSiteCd );      	//현장코드

       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "baseTm"  	, sBaseTm );      	//기본_시간
       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "extnTm"  	, sExtnTm );      	//연장_시간
       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "rmkCn"   	, sRmkCn );       	//비고
       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "horwgAmt"	, sHorwgAmt );    	//시급

       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "row"   	 	, row );       		//행
       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "col"   	 	, col );       		//열

       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "wkdGbCd"	,  sWkdGbCd);       //요일구분코드[CF046]
       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "yyvctYn"	,  sYyvctYn);       //연차여부

       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "extnExchTm"	,  0);       		//연장_할증_시간
       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "nghtTm"		,  0);       		//야간_시간
       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "nghtExchTm"	,  0);       		//야간_할증_시간
       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "hldyErndTm"	,  0);       		//휴일_근로_시간
       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "hldyTm"		,  0);      		//휴일_시간
       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "monholTm"	,  0);       		//월차_시간
       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "lbatnTm"	,  0);       		//출역_시간
       
       dsTcmLbatnPfmcTempList.setCellData( nRowIndex , "holidayYn"	,  sHolidayYn);     //휴일 유무

    };
