	/**************************************************************************
	 * [2] onpageload(초기화포함)
	 **************************************************************************/
	scwin.onpageload = function() {
	    //화면초기화 호출
	    scwin.fnInitial();
	};

	/** 화면초기화 **/
	scwin.fnInitial = function() {
		// 파일업로드 
        cmCom.setSite(wfmKeySiteInfo.getWindow());  //세션의 현장코드를 셋팅
        //목록 그리드 초기화
        grdList.initGrid();
        scwin.udcInit();
        //UI팀용 함수 호출
        com.commonUI();
        //기본 주소, 세부 주소 WIDTH 조정 
        com.getUdcComp("udcAddressInfo", "addrBasic").setStyle("width", "320px");  
        com.getUdcComp("udcAddressInfo", "addrDetail").setStyle("width", "200px");  
        //주소변경 후 수정하는 경우 dataset 변경처리 하여 저장이 가능하도록 수정
        com.getUdcComp("udcAddressInfo", "addrBasic").bind("onchange" , scwin.fnUdcAddressInfo_onchange);
        com.getUdcComp("udcAddressInfo", "addrDetail").bind("onchange", scwin.fnUdcAddressInfoDtl_onchange);
        //연락처변경 후 수정하는 경우 dataset 변경처리 하여 저장이 가능하도록 수정
        com.getUdcComp("udcTelNo","telNo").bind("onchange" , scwin.fnUdcTelNo_onchange);
        scwin.fnSearch();
	};

	/**************************************************************************
	 * [3] 사용자정의 함수
	 **************************************************************************/
  	 // udc 초기화
    scwin.udcInit = function(){
        // 주소
        com.setUdcValue("udcAddressInfo", "zipNo"     , "");
        com.setUdcValue("udcAddressInfo", "addrBasic" , "");
        com.setUdcValue("udcAddressInfo", "addrDetail", "");
        com.setUdcValue("udcTelNo", "telNo", "");  
    };

	/**************************************************************************
	 * [4] Callback
	 **************************************************************************/
    /* 장애인 첨부파일 callback */
    scwin.popDsprAflCallback = function(result){
        let rowDsprIndex = dsList.getRowPosition();
         
        dsList.setCellData( rowDsprIndex , "dsprAflId" , result.aflId ); // 장애인여부 파일ID
        dsList.setCellData( rowDsprIndex , "dsprAflNm" , result.aflNm ); // 장애인여부 파일명
        
        cmCom.grawAfl(gen_n1AflList, {
            "udcAflList":com.udcAflList(dsList.getCellData( rowDsprIndex , "dsprAflId" )),
            "liIdNm":"gen_n1Generator",
            "liAnchorIdNm":"btn_n1AddFile"
        });
    };	 
    
    /* 첨부파일 callback */
    scwin.popAflCallback = function(result){
        let rowAflIndex = dsList.getRowPosition();
        dsList.setCellData( rowAflIndex , "aflId" , result.aflId ); // 장애인여부 파일ID
        dsList.setCellData( rowAflIndex , "aflNm" , result.aflNm ); // 장애인여부 파일명
        
        cmCom.grawAfl(gen_n2AflList, {
            "udcAflList":com.udcAflList(dsList.getCellData( rowAflIndex , "aflId" )),
            "liIdNm":"gen_n2Generator",
            "liAnchorIdNm":"btn_n2AddFile"
        });
    };  
        
	/************************************************************************************
	 *  [5] Event 함수
	 ************************************************************************************/
    /**************************************************************************
     * Event: scwin.WFrameSiteInfo_change_callback
     * 기 능: 현장코드 변경 이벤트
     **************************************************************************/
	scwin.WFrameSiteInfo_change_callback = function() {	
		dsSearchMap.set( "siteCd" , wfmKeySiteInfo.getWindow().ibxKeySiteCd.getValue());
		dsSearchMap.set( "siteNm" , wfmKeySiteInfo.getWindow().ibxKeySiteNm.getValue());
        scwin.btnFind_onclick();
	};

    /**************************************************************************
     * Event: scwin.btnFind_onclick
     * 기 능: 조회 버튼 Click 이벤트
     **************************************************************************/
	scwin.btnFind_onclick = function() {
	    if (com.isEmpty(dsSearchMap.get("siteCd"))){
	        com.showMessage("CMW0025", "현장명"); //$1은(는) 필수입력입니다.
	        return false;
	    }
        
		scwin.fnSearch();
	};

    // 노무근로자 목록 조회
    scwin.fnSearch = function() {
        dsSearchMap.set("popType","N");     //조회 시 계좌번호 마스킹 처리
        dsSearchMap.set("blackListYn","N"); //조회 시 블랙리스트 여부 
        
        ajaxLib.ajax("/cm/cm6020/CM60200000U/selectTcmWtrList", {
            method : "POST",
            requestData : dsSearchMap.getJSON(),
            callback : function(result, e){      
                dsList.setJSON(result.data.dsList);
                com.setUdcValue("dsListCount"   , "udcListCount",  dsList.getRowCount());
                com.setUdcValue("udcTelNo"      , "telNo",         dsList.getCellData(0,"telNo"));     //전화번호
                com.setUdcValue("udcAddressInfo", "zipNo",         dsList.getCellData(0,"zipCd"));     //우편번호
                com.setUdcValue("udcAddressInfo", "addrBasic",     dsList.getCellData(0,"baseAddr"));  //기본주소
                com.setUdcValue("udcAddressInfo", "addrDetail",    dsList.getCellData(0,"dtlAddr"));   //상세주소
                
                //장애인여부 첨부파일
                cmCom.grawAfl(gen_n1AflList, {
                    "udcAflList":com.udcAflList(dsList.getCellData(0,"dsprAflId")),
                    "liIdNm":"gen_n1Generator",
                    "liAnchorIdNm":"btn_n1AddFile"
                });
                
                //첨부파일
                cmCom.grawAfl(gen_n2AflList, {
                    "udcAflList":com.udcAflList(dsList.getCellData(0,"aflId")),
                    "liIdNm":"gen_n2Generator",
                    "liAnchorIdNm":"btn_n2AddFile"
                });            
                    
                grdList.setFocusedCell( 0 , 1 , false );
                scwin.grdList_oncellclick(0, 1);   
                
                dsList.initRowStatus();    
                ibxBnactNo.setReadOnly(true);                               //계좌번호 
            },            
            errCallback: function(result, e) {
                console.log(result);
            }
        });
    };
    
	/**************************************************************************
     * 함 수 명: scwin.btnNew_onclick
     * 기 능:  신규버튼 Click 이벤트
     **************************************************************************/
	scwin.btnNew_onclick = function() {
        //현장코드 입력이 되어있는지 체크 
        if (com.isEmpty(dsSearchMap.get("siteCd")))
        {
            com.showMessage("CMW0025", "현장");   //$1은(는) 필수입력입니다.
            return;
        }
        
        let newDataChk = dsList.getModifiedIndex();    
        
        if(newDataChk.length != 0){
            com.showMessage("CSA0007"); //변경된 내용이 있습니다. 저장 후 진행하시기 바랍니다..
            return ;
        }
        
        let rowNewIndex = dsList.insertRow();
        
        grdList.setFocusedCell( rowNewIndex , "wkrNo" , false );  
        
        rdoBlackListYn.setReadOnly(false);                           //블랙리스트여부
        rdoRgacSbmtYn.setReadOnly(false);                            //등본제출여부
        rdoEplmtInsurEntrYn.setReadOnly(false);                      //고용보험여부
        rdoNatnpPnsnEntrYn.setReadOnly(false);                       //국민연금가입여부
        rdoHlthInsurEntrYn.setReadOnly(false);                       //건강보험여부
        rdoDsprYn.setReadOnly(false);                                //장애인여부
        ibxSecResno.setReadOnly(false);                              //주민번호
        ibxBnactNo.setReadOnly(false);                               //계좌번호
        
        //장애인 여부 첨부파일 초기화
        gen_n1AflList.removeAll();                                   //장애인여부 첨부파일 
        
        //첨부파일 초기화
        gen_n2AflList.removeAll();                                   //첨부파일 
        
        com.setUdcValue("udcTelNo"      , "telNo", "");              //전화번호
        com.setUdcValue("udcAddressInfo", "zipNo", "");              //우편번호 
        com.setUdcValue("udcAddressInfo", "addrBasic", "");          //기본주소
        com.setUdcValue("udcAddressInfo", "addrDetail", "");         //상세주소
        
        let sSiteCd = dsSearchMap.get("siteCd");
        
        dsList.setCellData( rowNewIndex , "blackListYn"      , "N");    //블랙리스트여부:아니오
        dsList.setCellData( rowNewIndex , "eplmtInsurEntrYn" , "N");    //고용보험여부:예
        dsList.setCellData( rowNewIndex , "natnpPnsnEntrYn"  , "N");    //국민연금가입여부:아니오
        dsList.setCellData( rowNewIndex , "hlthInsurEntrYn"  , "N");    //건강보험여부:아니오
        dsList.setCellData( rowNewIndex , "rgacSbmtYn"       , "Y" );   //등본제출여부:예
        dsList.setCellData( rowNewIndex , "dsprYn"           , "N" );   //장애인여부:아니오
        dsList.setCellData( rowNewIndex , "labrGbVal"        , "L" );   //임직/노무구분:직영
        dsList.setCellData( rowNewIndex , "ockndUct"         ,  0  );   //단가:0
        dsList.setCellData( rowNewIndex , "wkrNo"            , "" );    //근로자번호(개인번호)
        dsList.setCellData( rowNewIndex , "siteCd"           , sSiteCd);//현장(부서)코드
        dsList.setCellData( rowNewIndex , "resnoErrYn"       , "N");    //주민번호오류여부
        dsList.setCellData( rowNewIndex , "mmwgAmt"          , 0);      //월급금액
        dsList.setCellData( rowNewIndex , "horwgAmt"         , 0);      //시급금액
        
        ibxSecResno.focus();    //주민번호 포커스 이동
	};
	
	/**************************************************************************
     * 함 수 명: scwin.btnSave_onclick
     * 기 능:  저장버튼 Click 이벤트
     **************************************************************************/
    scwin.btnSave_onclick = function() {
        let dataChk 	= dsList.getModifiedJSON( true );
			
		if( dataChk.length == 0 ){
            com.showMessage("CSA0003"); //저장(변경)할 내용이 없습니다.
            return ;
        }
      
        let rowIndex = dsList.getRowPosition();
		let arrData  = dsList.getModifiedIndex();
        let chkVal = false;
        
		for(let si = 0; si < arrData.length; si++){
            dsList.setCellAllData( arrData[si] , "rowKey" , arrData[si] );	//에러발생시 위치
			let rowData = dsList.getRowJSON( arrData[si] );

			if(com.isEmpty(rowData.mskSecResno) && dsList.getRowStatus( arrData[si] ) == 'C'){
				com.showMessage("CMW0025", "주민번호");     //$1은(는) 필수입력입니다.
				ibxSecResno.focus();
                return false;
			}
            //주민번호 자릿수 체크 
            else if(!com.isEmpty(rowData.mskSecResno) && dsList.getRowStatus( arrData[si] ) == 'C')
            {
                let chkSecResno = rowData.mskSecResno;
                chkSecResno     = chkSecResno.replace("-","");
                
                if(chkSecResno.length < 13)
                {
                    com.showMessage("CSA0012", "13자리", "주민등록번호 등록이");  //$1만 $2 가능합니다.
                    ibxSecResno.focus();
                    return false;
                }
            }
            
            if(com.isEmpty(rowData.wkrNm)){
                com.showMessage("CMW0025", "출역자명");     //$1은(는) 필수입력입니다.
                ibxWkrNm.focus();
                return false;
            }      
            
            if(com.isEmpty(rowData.ockndCd)){
                com.showMessage("CMW0025", "직종");        //$1은(는) 필수입력입니다.
                ibxOckndCd.focus();
                return false;
            }   
            
            if(com.isEmpty(rowData.ockndUct) || rowData.ockndUct == 0){
                com.showMessage("CMW0025", "단가");        //$1은(는) 필수입력입니다.
                ibxOckndUct.focus();
                return false;
            } 
            
            if(com.isEmpty(rowData.labrGbVal)){
                com.showMessage("CMW0025", "임직/노무구분");//$1은(는) 필수입력입니다.
                sbxLabrGbVal.focus();
                return false;
            }
               
            if(com.isEmpty(rowData.bankNm)){
                com.showMessage("CMW0025", "은행명");      //$1은(는) 필수입력입니다.
                ibxBankNm.focus();
                return false;
            }                                                       
            
            if(com.isEmpty(rowData.bnactNo)){
                com.showMessage("CMW0025", "계좌번호");    //$1은(는) 필수입력입니다.
                ibxBnactNo.focus();
                return false;
            }
            
            if(com.isEmpty(rowData.achdrNm)){
                com.showMessage("CMW0025", "예금주");     //$1은(는) 필수입력입니다.
                ibxAchdrNm.focus();
                return false;
            }
            
            if(com.getUdcValue("udcTelNo", "telNo")  == ""){
                com.showMessage("CMW0025", "전화번호");   //$1은(는) 필수입력입니다.
                return false;
            }
            
            if (com.getUdcValue("udcAddressInfo", "zipNo") == "")
    		{
    			com.showMessage("CMW0025", "우편번호");	//$1은(는) 필수입력입니다.
                return;
    		}
    		
    		if (com.getUdcValue("udcAddressInfo", "addrBasic") == "")
    		{
    			com.showMessage("CMW0025", "주소");	   //$1은(는) 필수입력입니다.
    			return;
    		}
            
            //신규  저장 일 때 현장코드+주민번호로 같은 출역자가 있으면 저장 안되도록 유효성 로직 추가
            let rowChageIStatus = dsList.getRowStatus( arrData[si] );
            dsDupChkMap.set("siteCd"   , dsSearchMap.get("siteCd"));    //현장코드
            dsDupChkMap.set("secResno" , rowData.mskSecResno);          //주민번호
            
            if(rowChageIStatus == "C")
            {
                ajaxLib.ajax("/cm/cm6020/CM60200000U/selectDupChkYn", {
                method : "POST",
                mode : "synchronous",
                requestData : dsDupChkMap.getJSON(),
                    callback : function(result, e){      
                        dsDupResult.setJSON(result.data.dsDupResult);
                        if(dsDupResult.getCellData(0, "checkYn") == "Y")
                        {
                            chkVal = true;
                        }
                        else
                        {
                            dsList.setCellData( rowIndex , "telNo"    , com.getUdcValue("udcTelNo", "telNo"));              //전화번호
                            dsList.setCellData( rowIndex , "zipCd"    , com.getUdcValue("udcAddressInfo", "zipNo"));       //우편번호
                            dsList.setCellData( rowIndex , "baseAddr" , com.getUdcValue("udcAddressInfo", "addrBasic"));   //기본주소
                            dsList.setCellData( rowIndex , "dtlAddr"  , com.getUdcValue("udcAddressInfo", "addrDetail"));  //상세주소
                        }
                    },            
                    errCallback: function(result, e) {
                        console.log(result);
                    }
                });
            }
        }
       
        if(chkVal == false)
        {
            if (confirm("저장 하시겠습니까?")) {
                scwin.fnSave(); //저장 호출
            }
        }
        else
        {
            com.showMessage("CMW0001", "현장코드|주민등록번호"); //중복 된 $1가(이) 존재합니다.
        }
		
	};
    
    // 저장
    scwin.fnSave = function() {
        ajaxLib.ajax("/cm/cm6020/CM60200000U/insertTcmLabrWkr", {
                method : "POST",
                requestData : {
                    tcmLabrWkrList : dsList.getModifiedJSON()
                },
                callback : function(result, e){      
                    com.showMessage("CFA0012"); //저장되었습니다.
                    scwin.btnFind_onclick();
                    dsDupChkMap.set( "secResno" , "");
                },
                errCallback: function(result, e) {
                    com.showMessage("CFE0200","노무자기본사항");// $1 처리시 오류가 발생하였습니다.
                }
         });
    };
        
    /**************************************************************************
     * 함 수 명: scwin.btnDel_onclick
     * 기 능: 삭제버튼 Click 이벤트 
     **************************************************************************/
	scwin.btnDel_onclick = function() {
        let delRow = grdList.getFocusedRowIndex();	//삭제
        
		if (delRow < 0 || delRow == undefined) {
            com.showMessage("PRA0003");//삭제할 데이터를 선택해 주십시오.
            return;
        }
        
        /*신규 버튼 클릭하고 작업한 경우는 삭제 가능*/
        if(dsList.getRowStatus(delRow) == "C")
        {
            scwin.fnDelete(delRow);
            return;
        }
        
        /*삭제 전 체크*/
        /*화면에서 실제 삭제 처리를 하지 않고 첨부파일만 삭제하고 다시 재 진입시 || 파일 추가 후 삭제 처리 시  유효성 체크*/
        if (dsList.getCellData( delRow , "aflId") != "") 
        { 
            com.showMessage("MGA0018", "첨부파일 삭제 후 진행"); //$1 해주세요  
            return;
        }
        
        if (dsList.getCellData( delRow , "dsprAflId") != "") 
        { 
            com.showMessage("MGA0018", "장애인 첨부파일 삭제 후 진행"); //$1 해주세요  
            return;
        }
        
        dsDelMap.set( "siteCd"  , dsList.getCellData( delRow , "siteCd"));
        dsDelMap.set( "wkrNo"   , dsList.getCellData( delRow , "wkrNo" ));
        
        /*삭제전 노무투입사항 여부 확인*/
        ajaxLib.ajax("/cm/cm6020/CM60200000U/selectDelPreTcmWtr", {
            method : "POST",
            requestData : dsDelMap.getJSON(),
            callback : function(result, e){
                dsDelPreTcmWtr.setJSON(result.data.dsDelPreTcmWtr);
                
                let sel1 = dsDelPreTcmWtr.getCellData(0 ,"sel1"); // TCM_노무_출역_상세 일자
                let sel2 = dsDelPreTcmWtr.getCellData(0 ,"sel2"); // TCM_노무_출역_기타 일자
                
                if((!com.isEmpty(sel1) || !com.isEmpty(sel2)) && dsDelPreTcmWtr.getRowCount() == 1)
                {
                    com.showMessage("SAA0076", "출역자료가 있는 노무자"); //$1은(는) 삭제할 수 없습니다.
                    return;
                }
                else
                {
                    if (confirm("삭제 하시겠습니까?")) {
                        scwin.fnDelete(delRow); //삭제 호출
                    }                
                }  
            },
            errCallback : function(result, e) {
                com.showMessage("CFE0200","노무자 기본사항 출역자료 가져오는 부분");// $1 처리시 오류가 발생하였습니다.
            }
        });  
	};
	
	/**************************************************************************
     * 함 수 명: scwin.btnExcelDwn_onclick
     * 기 능: 엑셀다운버튼 Click 이벤트
     **************************************************************************/
    scwin.btnExcelDwn_onclick = function() {
        let result = [];
        //result.push(1);//checkbox 제외
       
        let totalCol = grdList.getTotalCol();
       
        for(let ex = 0; ex < totalCol; ex++) {
            if (!grdList.getColumnVisible(ex)) {
                result.push(ex);
            }
        }
        
        let excelOpt = {
             fileName: "노무자기본사항.xlsx"    //또는 xlsx 확장자 사용
            ,removeColumns: result.join(",")
            ,useSubTotal: true                //colMerge된 컬럼을 Merge해서 출력 할 지 여부
            ,colMergeTextAlign: "center"    //colMerge된 컬럼의 textAlign설정 (bottom, center, top 설정)
            //,useStyle: "true"
            ,indent: 1
            , headerFontColor : "#FFFFFF"    // Header Font Color
            , headerColor : "#D9D9D9"        // Header Back Color
            , subTotalColor : "#FCE4D6"      // SubTotal Back Color
            , footerColor : "#BDD7EE"         // Footer Color
        };  //excel download 옵션
        
