        //dsDelMap.set( "oriBnactNo" , dsList.getCellData( nRow , "oriBnactNo" )); //ORI 계좌번호
        
        ajaxLib.ajax("/cm/cm6020/CM60200000U/deleteTcmWtr", {
            method : "POST",
            requestData : dsDelMap.getJSON(),
            callback : function(result, e){
                    com.showMessage("CMW0035"); //삭제되었습니다.
                    scwin.btnFind_onclick();
            },
            errCallback : function(result, e) {
                com.showMessage("CFE0200","노무자기본사항");// $1 처리시 오류가 발생하였습니다.
            }
        });        
    }
    
