           ibxBnactNo.setReadOnly(false);
           ibxBnactUptNo.setReadOnly(true);
        }
        else
        {
           ibxBnactNo.setReadOnly(true);
           ibxBnactUptNo.setReadOnly(false); 
        }
        
        //장애인여부 첨부파일
        cmCom.grawAfl(gen_n1AflList, {
            "udcAflList":com.udcAflList(dsList.getCellData(info.rowIndex,"dsprAflId")),
            "liIdNm":"gen_n1Generator",
            "liAnchorIdNm":"btn_n1AddFile"
        });
        
        //첨부파일
        cmCom.grawAfl(gen_n2AflList, {
            "udcAflList":com.udcAflList(dsList.getCellData(info.rowIndex,"aflId")),
            "liIdNm":"gen_n2Generator",
            "liAnchorIdNm":"btn_n2AddFile"
        });       
	};
	
