        dsList.setCellData( dsList.getRowPosition() , "dtlAddr"  , com.getUdcValue("udcAddressInfo" , "addrDetail"));   //상세주소
    };
    
    /** 연락처 udc 변경 이벤트 **/
    scwin.fnUdcTelNo_onchange = function()
    {
        //udcphone.xml에 displayFormat에 com.phoneFormat 이 부분으로 인해 AS-IS는 연락처를 안적을때 -표기로만 표기하여 저장이 되는데 
        //TO-BE는 -표기를 하면 마우스를 클릭해야만 기존에 입력한 -가 화면에 보여 필수값인데 입력했는지 확인이 어려워 AS-IS처럼 화면에 표기되게 displayFormat 제거 
        com.getUdcComp("udcTelNo","telNo").setDisplayFormat("");
        dsList.setCellData(dsList.getRowPosition() , "telNo",com.getUdcValue("udcTelNo","telNo") );                     //연락처
    }
