/**************************************************************************
 * DataCollection description
 *	dsSearchMap : 검색조건 Map
 *  dsList: 
 **************************************************************************/
/**************************************************************************
 * [1] 전역변수 선언
 **************************************************************************/ 
/**************************************************************************
 * [2] onpageload(초기화포함)
 **************************************************************************/
 	scwin.initScreen = function() {
    };
    
    scwin.onpageload = function() {
        //조회기간 set
        dsSearchMap.set("frmWorkYm",$p.getCurrentServerDate( "yyyyMM" ));
        
        //노무 지급 계좌별 (orderby 때문에 정의)
        //AS-IS는 ORDER BY가 현장코드,작업년월,은행명,계좌번호,근로자번호순인데 계좌번호 암호화데이터를 가지고 정렬하므로
        //TO-BE와는 정렬 순서가 다르다.  AS-IS의 계좌번호를 복호화 된 걸로 ORDER BY 하면 TO-BE와 정렬이 맞음
        dsSearchMap.set("pgmType","A");
        
        
        com.commonUI();
        
        //grdList_Decrypt.hide();
    };
/**************************************************************************
 * [3] 사용자정의 함수
 **************************************************************************/
/**************************************************************************
 * [4] Callback
 **************************************************************************/
/************************************************************************************
 * [5] Event 함수
 ************************************************************************************/
    //현장코드 변경 이벤트
    scwin.WFrameSiteInfo_change_callback = function(){
        if(!com.isEmpty(dsSearchMap.get("frmWorkYm"))  )
        {
            scwin.btnFind_onclick();    
        }
    };
    
    
	//조회 클릭
	scwin.btnFind_onclick = function() {
        dsSearchMap.set("frmLbatnDt", dsSearchMap.get("frmWorkYm"));                    //검색 시작 날짜
        
        
        if (com.isEmpty(dsSearchMap.get("frmWorkYm"))){
            com.showMessage("CMW0025", "조회기간 From");    //$1은(는) 필수입력입니다.
            return false;
        }
        
        ajaxLib.ajax("/cm/cm6025/CM60250000U/selectTotalPaymentListMainCompany",{
            method : "POST",
            requestData : dsSearchMap.getJSON(),
            callback : function(result, e) {
                        dsList.setJSON(result.data.dsList);
                        com.setUdcValue("dsTotalCount","udcListCount",dsList.getTotalRow());
                        for(let si = 0; si < dsList.getRowCount(); si++)
                        {
                            let sLbatnQty = dsList.getCellData( si ,  "dailywork");
                            if(sLbatnQty.indexOf(".") == -1)
                            {
                                dsList.setCellData( si , "dailywork" , sLbatnQty + ".0");
                            }
                        }   
                       },
            errCallback : function(result, e) {}
        });
        
             
	};
    
    
    scwin.excelDwn = function() {
        let result = [];
        //let totalCol = grdListDecrypt.getTotalCol();
        let totalCol = grdList.getTotalCol();
        
        
        for(let i = 0; i < totalCol; i++) {
            //if (!grdListDecrypt.getColumnVisible(i)) {
            if (!grdList.getColumnVisible(i)) {
                result.push(i);
            }
        }
        
        let excelOpt = {
             fileName: "본사-노무비지급총괄표(계좌별).xlsx"    //또는 xlsx 확장자 사용
            ,removeColumns: result.join(",")
            ,useSubTotal: true                //colMerge된 컬럼을 Merge해서 출력 할 지 여부
            ,colMergeTextAlign: "center"      //colMerge된 컬럼의 textAlign설정 (bottom, center, top 설정)
            ,colMerge : false
            ,useStyle: "true"
            ,indent: 1
            ,useDataFormat : true
            , headerColor : "#D9D9D9"        // Header Back Color
            , subTotalColor : "#FCE4D6"      // SubTotal Back Color
            , footerColor : "#BDD7EE"         // Footer Color
        };  //excel download 옵션
        
        
        grdListDecrypt.advancedExcelDownload( excelOpt );   
        scwin.savePrivUseHist();
               
    }

    //엑셀다운
	scwin.btnExcelDwn_onclick = function() {
		
        if (ibxDownloadReason.getValue() == "")
        {
            com.showMessage("CMW0025", "사유");    //$1은(는) 필수입력입니다.;
            return false;
        }


        
        dsSearchMap.set("frmLbatnDt", dsSearchMap.get("frmWorkYm"));                    //검색 시작 날짜
        
        if (com.isEmpty(dsSearchMap.get("frmWorkYm"))){
            com.showMessage("CMW0025", "조회기간 From");    //$1은(는) 필수입력입니다.
            return false;
        }
        
        ajaxLib.ajax("/cm/cm6025/CM60250000U/selectTotalPaymentList_Decrypt",{
            method : "POST",
            requestData : dsSearchMap.getJSON(),
            callback : function(result, e) {
                        dsList_Decrypt.setJSON(result.data.dsList_Decrypt);
                        com.setUdcValue("dsTotalCount","udcListCount",dsList_Decrypt.getTotalRow());
                        for(let si = 0; si < dsList_Decrypt.getRowCount(); si++)
                        {
                            let sLbatnQty = dsList_Decrypt.getCellData( si ,  "dailywork");
                            if(sLbatnQty.indexOf(".") == -1)
                            {
                                dsList_Decrypt.setCellData( si , "dailywork" , sLbatnQty + ".0");
                            }
                        } 
                        scwin.excelDwn();  
                       },
            errCallback : function(result, e) {}
        });
        
	};
    
    scwin.savePrivUseHist = function()
    {
        dsPrivacyUseHist.set("menuId", "CM60250056U" );
        dsPrivacyUseHist.set("prvDocGb", "EXCEL");
        dsPrivacyUseHist.set("prvUseDesc", "이런저런 파라메터");
    
        ajaxLib.ajax("/cf/insertTcfPrivacyUseHist",{
                method : "POST",
                requestData : dsPrivacyUseHist.getJSON(),
                callback : function(result, e){      
                        scwin.btnFind_onclick();
                },
                 errCallback: function(result, e) {
                    com.showMessage("CFE0200","개인정보사용이력 저장");// $1 처리시 오류가 발생하였습니다.
                }
        });                    
    }
    
    
	
    scwin.subtotal_function = function(tc,val)
    {
        let rtnVal = "";
        let currRow = -1;
        let compVal = "";
        
        if(val == 1)      //계좌번호
        {
            rtnVal  = tc;
            compVal = rtnVal.split(':');
            rtnVal  = compVal[0]
        }
        else if(val == 2) //은행명
        {
            currRow = dsList.getMatchedIndex( "bnactNo" , tc , true , 0 , dsList.getTotalRow())[0];
            rtnVal = dsList.getCellData( currRow , "bankName");
        }
        else if(val == 3)             //예금주
        {
            currRow = dsList_Decrypt.getMatchedIndex( "bnactNo" , tc , true , 0 , dsList_Decrypt.getTotalRow())[0];
            rtnVal = dsList_Decrypt.getCellData( currRow , "achdrNm");
        }
        else if (val == 4) // 현장
        {
            currRow = dsList_Decrypt.getMatchedIndex( "siteCd" , tc , true , 0 , dsList_Decrypt.getTotalRow())[0];
            rtnVal = dsList_Decrypt.getCellData( currRow , "siteNm");
        }
        
        return rtnVal;
    }
    
    scwin.subtotalDecrypt_function = function(tc,val)
    {
