    scwin.onpageload = function() {       
        scwin.btnFind_onclick();
        
        //화면초기화 호출
        scwin.fnInitial();
    };
    
    scwin.onpageunload = function() {};

    /* 화면초기화 */
    scwin.fnInitial = function() {
    	
    	//공통코드: 은행코드[CF034]
		com.selCdList("bankCd", "CF034", "Y");
        
        //공통코드: 거래처구분값
        com.selCdListVrbl("tradeGbVal", {
            "upperCd": "CM100023",
            "setCd": "charValN1",
            }
        );
		
		//UI팀용 함수 호출
        scwin.commonUI();
    };
    
    /************************************************************************************
     *  [3] 사용자정의 함수
     ************************************************************************************/
    /* 중복체크 */
    scwin.fnSelectDupChk = function(){
        ajaxLib.ajax("/cm/cm7000/CM70000010U/selectDupChk", {
            mode: "synchronous",
            method: "Post",
            requestData: {"bzprRegNo" : dsTcmEqpmHireVendrMap.get( "bzprRegNo" ),
                          "secResno" : dsTcmEqpmHireVendrMap.get( "secResNo" )},
            callback: function(result,e) {
                           if(result.data == "new"){
                               com.showMessage("SCA0061", "번호"); //사용가능한 $1입니다.
                           }else{
                               //조회
                               scwin.selectHireVendr(result.data);
                           }
                           btnDupChkBzprRegNo.setDisabled( true );
                           btnDupChkSecResNo.setDisabled( true );
            }, 
            errCallback: function(result,e) {}
        });
    };
    
    /* 임차업체 상세정보 조회 */
    scwin.selectHireVendr = function(tradeCd){
        let param = new Object();
        param.tradeCd = tradeCd;
        
        ajaxLib.ajax("/cm/cm7000/CM70000010U/selectHireVendrDtl", {
            method: "POST",
            requestData: param,
            callback: function(result,e) {
                       dsTcmEqpmHireVendrMap.setJSON(result.data);
                       
                       //본점사업자번호 활성화, 비활성화
                       if (dsTcmEqpmHireVendrMap.get("tradeGbVal") == "1") { //사업자구분이 사업자번호 
                            mstoBzprRegNo.setDisabled(false);
                            bzprRegNo.setDisabled(true);
                            secResNo.setDisabled(true);
                            tradeGbVal.setDisabled(true);
                       }else if (dsTcmEqpmHireVendrMap.get("tradeGbVal") == "2") { //사업자구분이 주민번호
                            mstoBzprRegNo.setDisabled(true);
                            bzprRegNo.setDisabled(true);
                            secResNo.setDisabled(true);
                            tradeGbVal.setDisabled(true);
                       }
                
                       com.setUdcValue("udcTelNo", "telNo", dsTcmEqpmHireVendrMap.get("telNo"));            //전화번호
                       com.setUdcValue("udcAddress", "zipNo", dsTcmEqpmHireVendrMap.get("zipCd"));          //우편번호
                       com.setUdcValue("udcAddress", "addrBasic", dsTcmEqpmHireVendrMap.get("baseAddr"));   //기본주소
                       com.setUdcValue("udcAddress", "addrDetail", dsTcmEqpmHireVendrMap.get("dtlAddr"));   //상세주소
                                        
                       //사업자등록증 첨부파일
                       cmCom.grawAfl(gen_n1AflList, {
                           "udcAflList": com.udcAflList(dsTcmEqpmHireVendrMap.get("n1AflId")),
                            "liIdNm": "gen_n1Generator",
                            "liAnchorIdNm": "btn_n1AddFile"
                       });
                        
                       //통장사본 첨부파일
                       cmCom.grawAfl(gen_n2AflList, {
                           "udcAflList": com.udcAflList(dsTcmEqpmHireVendrMap.get("n2AflId")),
                           "liIdNm": "gen_n2Generator",
                           "liAnchorIdNm": "btn_n2AddFile"
                       });
                        
                       //계약서 첨부파일
                       cmCom.grawAfl(gen_n3AflList, {
                           "udcAflList": com.udcAflList(dsTcmEqpmHireVendrMap.get("n3AflId")),
                           "liIdNm": "gen_n3Generator",
                           "liAnchorIdNm": "btn_n3AddFile"
                       });
                        
                       //일반 첨부파일
                       cmCom.grawAfl(gen_n4AflList, {
                           "udcAflList": com.udcAflList(dsTcmEqpmHireVendrMap.get("n4AflId")),
                           "liIdNm": "gen_n4Generator",
                           "liAnchorIdNm": "btn_n4AddFile"
                       });
                        
                        //변경 내용 없을 때 저장할 내용 없음 표시(상세 조회후 row 상태 초기화)
                        dsTcmEqpmHireVendrMap.initRowStatus(); 
                        
            },
            errCallback : function(result,e) {}
        });
        
    };
     
    /************************************************************************************
     *  [4] Callback 함수
     ************************************************************************************/
    /* 사업자등록증 첨부파일 Callback 처리 */
	scwin.popN1AflCallback = function(result){
		dsTcmEqpmHireVendrMap.set("n1AflId",  result.aflId);
		dsTcmEqpmHireVendrMap.set("n1AflNm",  result.aflNm);
		
		cmCom.grawAfl(gen_n1AflList, {
			"udcAflList": com.udcAflList(dsTcmEqpmHireVendrMap.get("n1AflId")),
			"liIdNm": "gen_n1Generator",
			"liAnchorIdNm": "btn_n1AddFile"
		});
	};
	
	/* 통장사본 첨부파일 Callback 처리 */
	scwin.popN2AflCallback = function(result){
