    /************************************************************************************
     *  [5] Event 함수
     ************************************************************************************/
    /* 조회버튼 클릭 */
    scwin.btnFind_onclick = function(){ 
        ajaxLib.ajax("/cm/cm7000/CM70000010U/selectTcmEqpmHireVendrList", {
            method: "POST",
            requestData: dsSearchMap.getJSON(),
            callback: function(result,e) {
                        dsTcmEqpmHireVendrList.setJSON( result.data );
                        com.setUdcValue("dsTotalCount","udcListCount",dsTcmEqpmHireVendrList.getTotalRow());
                        
                        tradeGbVal.setDisabled( true );
                        bzprRegNo.setReadOnly( true );
                        btnDupChkBzprRegNo.setDisabled( true );
                        btnDupChkSecResNo.setDisabled( true );
                        
                        scwin.selectHireVendr(dsTcmEqpmHireVendrList.getCellData( 0 , "tradeCd" ));
            },
            errCallback: function(result,e) {}
        });
        
    };
    
    /* 신규 */
	scwin.btnNew_onclick = function() {
		// map 초기화
        dsTcmEqpmHireVendrMap.setEmptyValue();
        
        grpEqpmVendr.setDisabled( false );
        bzprRegNo.setReadOnly( false );
        btnDupChkSecResNo.setDisabled( true );
        
        com.setUdcValue("udcTelNo", "telNo", "");        //전화번호
        com.setUdcValue("udcAddress", "zipNo", "");      //우편번호 
        com.setUdcValue("udcAddress", "addrBasic", "");  //기본주소
        com.setUdcValue("udcAddress", "addrDetail", ""); //상세주소
        
        //첨부파일 초기화
        gen_n1AflList.removeAll(); //사업자등록증 
        gen_n2AflList.removeAll(); //통장사본
        gen_n3AflList.removeAll(); //계약서 
        gen_n4AflList.removeAll(); //일반 
	};
    
    /* 사업자구분 변경 이벤트 */
    scwin.tradeGbVal_onchange = function() {
        if (dsTcmEqpmHireVendrMap.get("tradeCd") == "") { //신규일 경우에만 실행
            if (dsTcmEqpmHireVendrMap.get("tradeGbVal") == "1") { //사업자구분이 사업자번호일 경우
                bzprRegNo.setReadOnly(false);
                secResNo.setValue("");
                secResNo.setReadOnly(true);
                btnDupChkBzprRegNo.setDisabled(false);
                btnDupChkSecResNo.setDisabled(true);
                mstoBzprRegNo.setDisabled(false);
            }else {                                               //사업자구분이 주민번호일 경우
                bzprRegNo.setValue("");
                bzprRegNo.setReadOnly(true);
                secResNo.setReadOnly(false);
                btnDupChkBzprRegNo.setDisabled(true);
                btnDupChkSecResNo.setDisabled(false);
                mstoBzprRegNo.setDisabled(true);
            }
        }
    };
	
	/* 저장 */
	scwin.btnSave_onclick = function() {       
        //udc매핑
        dsTcmEqpmHireVendrMap.setJSON({
            "telNo": com.getUdcValue("udcTelNo", "telNo"),          //전화번호
            "zipCd": com.getUdcValue("udcAddress", "zipNo"),        //우편번호
            "baseAddr": com.getUdcValue("udcAddress", "addrBasic"), //기본 주소
            "dtlAddr": com.getUdcValue("udcAddress", "addrDetail")  //상세 주소
        });
        
        let nTradeGbVal = dsTcmEqpmHireVendrMap.get("tradeGbVal");  //거래처구분값

        if (nTradeGbVal == "") {
            com.showMessage("CMW0025", "사업자구분");    //$1은(는) 필수입력사항입니다.
            return false;
        }else {
            if (nTradeGbVal == "1") {
                if (dsTcmEqpmHireVendrMap.get("bzprRegNo") == "") {
                    com.showMessage("CMW0025", "사업자번호");    //$1은(는) 필수입력사항입니다.
                    return false;
                }
                //사업자구분이 사업자번호인 경우 법인등록번호 필수 체크
//                if (strLib.isEmpty(dsTcmEqpmHireVendrMap.get("corpRegNo"))) {
//                    com.showMessage("SAA0043", "법인사업자의 경우 법인등록번호", "", "", corpRegNo.id);  //$1은(는) 필수입력사항입니다.
//                    return false;
//                }
            }else if (nTradeGbVal == "2") {
                if (dsTcmEqpmHireVendrMap.get("secResNo") == "") {
                    com.showMessage("CMW0025", "주민등록번호");    //$1은(는) 필수입력사항입니다.
                    return false;
                }    
            }
        }
        
        if (!btnDupChkBzprRegNo.getDisabled()) {
            com.showMessage("SCA0084", "사업자번호"); //$1 중복체크를 진행해 주십시오.
            return;
        }else if (!btnDupChkSecResNo.getDisabled()) {
            com.showMessage("SCA0084", "주민등록번호"); //$1 중복체크를 진행해 주십시오.
            return;
        }
        
        if (dsTcmEqpmHireVendrMap.get( "hireTradeNm" ) == "") {
			com.showMessage("CMW0025", "회사명");	//$1은(는) 필수입력사항입니다.
			return false;
		}
        if (dsTcmEqpmHireVendrMap.get( "bankCd" ) == "") {
            com.showMessage("CMW0025", "은행명");  //$1은(는) 필수입력사항입니다.
            return false;
        }
        if (dsTcmEqpmHireVendrMap.get( "secBnactNo" ) == "") {
            com.showMessage("CMW0025", "계좌번호");  //$1은(는) 필수입력사항입니다.
            return false;
        }
        if (dsTcmEqpmHireVendrMap.get( "achdrNm" ) == "") {
            com.showMessage("CMW0025", "예금주");  //$1은(는) 필수입력사항입니다.
            return false;
        }
        if (dsTcmEqpmHireVendrMap.get( "reprNm" ) == "") {
            com.showMessage("CMW0025", "대표자");  //$1은(는) 필수입력사항입니다.
            return false;
        }
        if (dsTcmEqpmHireVendrMap.get( "secCrryTelNo" ) == "") {
            com.showMessage("CMW0025", "핸드폰번호");  //$1은(는) 필수입력사항입니다.
            return false;
        }
        if (dsTcmEqpmHireVendrMap.get( "email" ) == "") {
            com.showMessage("CMW0025", "이메일");  //$1은(는) 필수입력사항입니다.
            return false;
        }
        
        let reg_email = /^([0-9a-zA-Z_\.-]+)@([0-9a-zA-Z_-]+)(\.[0-9a-zA-Z_-]+){1,2}$/; //이메일 체크 정규식
        
        if (dsTcmEqpmHireVendrMap.get("email") != "") {
            if (!reg_email.test(dsTcmEqpmHireVendrMap.get("email"))) { 
                com.showMessage("CMW0171"); //메일 주소를 형식에 맞게 입력하십시오.                      
                return false;         
            }
        }
        
		if (confirm("저장 하시겠습니까?")) {
	        ajaxLib.ajax("/cm/cm7000/CM70000010U/saveTcmEqpmHireVendr", {
	            mode: "synchronous",
	            method: "POST",
	            requestData: {
                               "tcfTradeInfoVO": dsTcmEqpmHireVendrMap.getJSON(),
                               "dsTcmEqpmHireVendrMap": dsTcmEqpmHireVendrMap.getJSON()
                },
	            callback: function(result,e) {
                            com.showMessage("CFA0012"); //저장되었습니다.
                            scwin.btnFind_onclick();
                },
	            errCallback: function(result,e) {}
	        });
	    }
	};

	/* 임차업체 목록 클릭(상세조회) */
	scwin.grdTcmEqpmHireVendrList_oncellclick = function(row,col) {
		if (dsTcmEqpmHireVendrList.getRowCount() > 0) {
            let idxRow = grdTcmEqpmHireVendrList.getFocusedRowIndex();
            var selTradeCd = dsTcmEqpmHireVendrList.getCellData(idxRow , "tradeCd");
		    
            tradeGbVal.setDisabled( true );
            bzprRegNo.setReadOnly( true );
            secResNo.setReadOnly( true );
            btnDupChkBzprRegNo.setDisabled( true );
            btnDupChkSecResNo.setDisabled( true );
        }
        scwin.selectHireVendr(selTradeCd);
	};
    
    /* 엑셀다운 */
    scwin.btnExcelDwn_onclick = function() {
        grdTcmEqpmHireVendrList.advancedExcelDownload({
            fileName: "임차업체목록.xlsx",
            useSubTotal : true,             // Sub-Total 표시 
            headerFontColor : "#FFFFFF",    // Header Font Color
            headerColor : "#D9D9D9",        // Header Back Color
            subTotalColor : "#FCE4D6",      // SubTotal Back Color
            footerColor : "#BDD7EE"         // Footer Color
        });
    };

	/* 사업자등록증 첨부파일 */
	scwin.btnN1Afl_onclick = function() {
		let sAflId = dsTcmEqpmHireVendrMap.get("n1AflId");	// 첨부파일ID
		let sJobGbCd = "CM";								// 업무구분
		let sMenuId = "CM70000010U";						// 매뉴ID
		
	 	let popOps = {
        			   pid: "AflPopup"+"_" +new Date().getTime(),
        		       url:"/ux/cf/CF00350000P.xml",
        		       modal : true, //true || false
        		       popup_name:"사업자등록증 첨부파일",
        		       callbackFn: "scwin.popN1AflCallback",
        		       widType: "M",
        		       height: 540,
        		       data: {
        		               aflId: sAflId,
        		               jobGbCd: sJobGbCd,
        		               menuId: sMenuId,
        		               fileExt: "*.*",
        		               usage: "INS"
        		             }
                     };
  		com.popup_open(popOps);
	};
	

