	/* 사업자번호 입력시 본점사업자번호에 동시 입력 */
	scwin.bzprRegNo_onchange = function() {
        if (dsTcmEqpmHireVendrMap.get("tradeCd") == "") { //신규일 경우에만 동시입력, 조회시에는 x.
            dsTcmEqpmHireVendrMap.set("mstoBzprRegNo" , dsTcmEqpmHireVendrMap.get( "bzprRegNo" ));
        }
	};
	
    /* 사업자번호 중복체크 */
	scwin.btnDupChkBzprRegNo_onclick = function() {
		let bzprRegNo = dsTcmEqpmHireVendrMap.get("bzprRegNo");
        
        bzprRegNo = bzprRegNo.trim();
        
        if (bzprRegNo == "") {
            com.showMessage("CFA0017", "사업자번호"); //$1을(를) 입력하십시오.
            return;
        }else {
            scwin.fnSelectDupChk();
        }
	};
	
    /* 주민등록번호 중복체크 */
	scwin.btnDupChkSecResNo_onclick = function() {
		let secResNo = dsTcmEqpmHireVendrMap.get( "secResNo" );
        
        secResNo = secResNo.trim();
        
        if (secResNo == "") {
            com.showMessage("CFA0017", "주민등록번호"); //$1을(를) 입력하십시오.
            return;
        }else {
            scwin.fnSelectDupChk();
        }
	};
    
    /* 주민등록번호 입력시 정상 여부 체크 */
    scwin.secResNo_onviewchange = function(info) {
        var chkResNo = /^(?:[0-9]{2}(?:0[1-9]|1[0-2])(?:0[1-9]|[1,2][0-9]|3[0,1]))-[1-8][0-9]{6}$/;

        if(!chkResNo.test(secResNo.getValue())) {
            com.showMessage("CFA0019", "주민등록번호", "", "", secResNo.id);  //$1을(를) 확인하십시오.
            secResNo.setValue("");
        }
    };
    
    /* 회사명 엔터 이벤트 */
	scwin.ibxKeyTradeNm_onkeydown = function(e) {
		if (e.keyCode == 13){
            dsSearchMap.set( "hireTradeNm" , ibxKeyTradeNm.getValue() );
            scwin.btnFind_onclick();
        }
	};
	
    /* 사업자번호 입력시 정상 체크 */
	scwin.bzprRegNo_onviewchange = function(info) {
		if (!com.chkBizNo(bzprRegNo.getValue())){
            com.showMessage("CFA0019", "사업자번호", "", "", bzprRegNo.id);  //$1을(를) 확인하십시오.
            bzprRegNo.setValue("");
        }
	};
	
    /* 본점사업자번호 입력시 정상 체크 */
	scwin.mstoBzprRegNo_onviewchange = function(info) {
		if (!com.chkBizNo(mstoBzprRegNo.getValue())){
            com.showMessage("CFA0019", "본점사업자번호", "", "", mstoBzprRegNo.id);  //$1을(를) 확인하십시오.
            mstoBzprRegNo.setValue("");
        }
	};
	
    /* 법인등록번호 입력시 정상 체크 */
	scwin.corpRegNo_onviewchange = function(info) {
		if (!com.chkCorpNo(corpRegNo.getValue())){
            com.showMessage("SCA0025", "등록번호가", "", "", corpRegNo.id); //$1 올바르지 않습니다.
            corpRegNo.setValue("");
        }
	};
