	scwin.grd_gridView1_oncellclick = function(row,col) {
		
		if ( row == 0 && col == 0 ) {
		    scwin.fnAflPop("INS");
		    alert("rtn:"+rtn);
		}
		if ( row == 0 && col == 1 ) {
		    scwin.fnAflPop("");
		    alert("rtn:"+rtn);
		}
	};
	
	scwin.fnAflPop = function( strIns ) {
		var popOps = {
			popup_name:"파일첨부 검색"
	               		,url:"/ux/cf/CF00350000P.xml"
	               		,data :  {
					              jobGbCd : "CF", 
					              menuId  : "CS05101525U", 
					              aflId   : "_20140103_102158314", 
					              fileExt : "txt,xlsx,doc,pdf",
					              usage   : strIns
	               		          }
	               		,widType : "M"
	               		,height:"520"
	               		,callbackFn : "scwin.popCallback"
	    };
		com.popup_open(popOps);	
	}

	// 팝업 결과 후처리
	scwin.popCallback = function(result){

    	var pRowIndex = 0;
    	pRowIndex = grd_gridView1.getFocusedRowIndex();
    	if (pRowIndex == -1){
    	    return;
    	}else{
			dataList1.setCellData(pRowIndex, "aflId", result.aflId);
			dataList1.setCellData(pRowIndex, "aflNm", result.aflNm);
    	}
	};

    /*
    * 날짜입력 예제(utils.js 의 com.transDateType(value, sdate))
    *
    * com.transDateType("frDay7")            : 일주일전 시작(오늘날짜 기본값)
    * com.transDateType("frDay7",new Date()) : Date object로 입력
    * com.transDateType("frDay7","20190415") : String으로 날짜입력
    */
    scwin.ins_lastWeek_onclick = function(value){
        com.setUdcValue( "udcPeriodcalendar", "frDate", com.transDateType("fr"+value));
        com.setUdcValue( "udcPeriodcalendar", "toDate", com.transDateType("to"+value));
    };

	// HTML2CANVAS 캡처 버튼 클릭 이벤트 핸들러
	scwin.btnPrintCanvas_onclick = function(e) {
		debugger;
		var srcImg = $("#" + aprvContent.id);
		
		html2canvas(srcImg, {
			onrendered : function(canvas) {
				var imgData = canvas.toDataURL();
				/*
				popupLib.open("popup0", "/ux/cf/html2canvas.xml", {
					title: "HTML2CANVAS 캡처",
					useLayer: false,
					modal: false,
					width:"800px",
					height:"680px",
					param: {
						img: imgData
					},
					scrollbars: true,
					resizable: true											
				});
				 */

				var popOps = {
					id : "popup0",
					url : "/ux/template/pagePrint/html2canvas.xml",
					type : "browser",
					popup_name : "HTML2CANVAS 캡처",
					data : {
						img : imgData
					}
				};
				com.popup_open(popOps);

			}
		});

	};
