head.script:
	var svcId = "";
	var popupId = "";

    /************************************************************************************
     *  [2] onpageload
     ************************************************************************************/
    scwin.onpageload = function() {
	    var nowDate  = $w.getCurrentServerDate( "yyyyMMdd" );

	    //전송일자 default set
	    com.setUdcValue("udcCalendarSrch", "frDate", $w.dateAdd( nowDate , "-7" ));
	    com.setUdcValue("udcCalendarSrch", "toDate", nowDate);
    };

	/**************************************************************************
	 *  [3] 사용자정의 함수
	 **************************************************************************/
	/**************************************************************************
	 * Event: scwin.fnSearchList
	 * 기 능: SMS 전송내역을 리스트 조회
	 **************************************************************************/
    scwin.fnSearchList = function() {
        // udc의 코드값 가져오기.
        dsSrch.set("frDate", com.getUdcValue("udcCalendarSrch","frDate"));
        dsSrch.set("toDate", com.getUdcValue("udcCalendarSrch","toDate"));

        svcId = "selectSdkSmsReportList";

        ajaxLib.ajax("/cf/CF00352001U/selectSdkSmsReportList", {
        	mode : "asynchronous"
        	, method : "POST"
            , requestData : dsSrch.getJSON()
            , callback : "fnCallback"
            , errCallback : "fnErrCallBack"
        });
    };


	/**************************************************************************
	 *  [4] Callback
	 **************************************************************************/
	// Popup Callback
	scwin.fnPopupCallback = function(arg) {

	alert("Popup Callback : " + popupId);

	   	switch(popupId) {
	   		case "intAprvPopup" :
				alert("intAprvPopup 호출 결과 : [" + arg.intAprvNo + "]");
				break;
	   		case "sendSmsPopup" :
				alert("sendSmsPopup 호출 결과");
				break;
		}
	}

	// 결과 후처리
	scwin.fnCallback = function(result, e) {
	   	switch(svcId) {
	   		case "selectSdkSmsReportList" :
				dsSmsReportList.setJSON(result.data.dsSdkSmsReportList);
				com.setUdcValue("dsTotalCount", "udcListCount", dsSmsReportList.getTotalRow());
		   		ingStatus = "S";

		   		for (var i = 0; i < dsSmsReportList.getTotalRow(); i++) {
		   			$('.activeGrid .grid_body_row').eq(i).addClass('active');
		   		}

	   			break;

	   		case "checkSlipJournalizing" :
	   			alert("EAI 전송되었습니다.");
	   			break;

	   		case "checkReverseJournalizing" :
	   			alert("EAI 전송되었습니다.");
	   			break;
	   }
	};

	// 에러 후처리
    scwin.fnErrCallBack = function(e) {
        console.log(e);
    }

    /************************************************************************************
     *  [5] Event 함수
     ************************************************************************************/
	/**************************************************************************
	 * Event: scwin.btnSend_onclick
	 * 기 능: SMS 전송내역을 리스트 조회
	 **************************************************************************/
	scwin.btnFind_onclick = function() {
        dsSmsReportList.clear();
		scwin.fnSearchList();
	};

	//수신자번호 엔터시
	scwin.ibxKeyPhoneNumber_onkeyup = function(e) {
		if ( e.keyCode == 13 ){
			btnFind.trigger("onclick");
		}
	};

	//발신자번호 엔터시
	scwin.ibxKeyCallBack_onkeyup = function(e) {
		if ( e.keyCode == 13 ){
			btnFind.trigger("onclick");
		}
	};

	//사번 엔터시
	scwin.ibxKeyUserId_onkeyup = function(e) {
		if ( e.keyCode == 13 ){
			btnFind.trigger("onclick");
		}
	};

	scwin.btnSmsSend_onclick = function() {
		var title = "SMS 전송";
		var id = "openTarget";
		var url = "/ux/template/tabTest/openTarget.xml";
		var openOpt = ibxTabTest.getValue();
		var data = {
					tabData:"탭으로 오픈"
				  	,browserData:"Browser로 오픈"
					};

        popupId = "sendSmsPopup";

		com.detailOpen(title, id, url, openOpt, data);
	};

	scwin.btnApro_onclick = function() {

		var popOps = {
				popup_name:"승인요청"
		               		,url:"/ux/cf/CF00350502P.xml"
   						    ,data:{ gubunCd:gcm.APPR_REQUEST		// gcm.APPR_REQUEST, gcm.APPR_PROC, gcm.APPR_FIND
				                  , intAprvNo:""
				                  , cancelYn:"" 			// 승인을 취소할 수 있는 경우 “Y”
				                  , intAprvTtl:"내부결제 테스트"			// 내부결재제목
					        	  , intAprvReqOpinCn:""		// 내부_결재_요청_의견_내용
					        	  , apprEmpno:"A00004"			// 승인자사번
					        	  , apprEmpNm:"테스트"			// 승인자명
					        	  , intAprvAproOpinCn:""	// 내부_결재_승인_의견_내용
					        	  , jobGbCd:""				// 업무구분코드
					        	  , menuId:""				// 메뉴ID
					        	  , scrUrl:""				// 화면_URL
	      						  }
					        ,width:"600"
					        ,height:"500"
		               		,callbackFn:"scwin.fnPopupCallback"
		    };

		popupId = "intAprvPopup";

		com.popup_open(popOps);
	};

	scwin.btnTest_onclick = function() {

        svcId = "checkSlipJournalizing";

		dsTcfSlipMaster.reset();
		dsTcfSlipMaster.reform();

		dsTcfSlipDetail.removeAll();
		dsTcfSlipDetail.reform();

		scwin.setMaster();
		scwin.setDetail();

        ajaxLib.ajax("/cf/cf0520/CF05200000E/checkSlipJournalizing", {
        	mode : "asynchronous"
        	, method : "PUT"
            , requestData: {
                    "tcfSlipMasterVO": dsTcfSlipMaster.getJSON()
                  , "tcfSlipDetailVO": dsTcfSlipDetail.getAllJSON()
                }
            , callback : "fnCallback"
            , errCallback : "fnErrCallBack"
        });
	};

	scwin.setMaster = function() {
        var nowDate = $w.getCurrentServerDate("yyyyMMdd");

		dsTcfSlipMaster.set("jobGbn"		, "C");		// 업무구분 C : CM (공사_공동도급)
		dsTcfSlipMaster.set("sendFlag"		, "Y");		// Y : EAI 전송, N : 미전송

		dsTcfSlipMaster.set("pstngDate"	, nowDate);

		if (svcId == "checkSlipJournalizing") {
			dsTcfSlipMaster.set("slipJnlzTypeCd", "SA0010");	// 전표분개유형

			dsTcfSlipMaster.set("acDocNo"	, "C190403-150278B-0001");
			dsTcfSlipMaster.set("headerTxt"	, "19년04월 공동원가배분");
			dsTcfSlipMaster.set("docDate"	, nowDate);
			dsTcfSlipMaster.set("zzissid"	, "1048131309137811078820");
			dsTcfSlipMaster.set("zzapprno"	, "");
			dsTcfSlipMaster.set("zzdeptCode", "K400120");
			dsTcfSlipMaster.set("zzdeptName", "자금팀");
			dsTcfSlipMaster.set("zzusrId"	, "tpkpg");
			dsTcfSlipMaster.set("zzusrName"	, "김판규");
			dsTcfSlipMaster.set("orgSlipNo"	, "");
			dsTcfSlipMaster.set("reasonRev"	, "");
		} else {
			dsTcfSlipMaster.set("acDocNo"	, "C190403-150278B-0002");
			dsTcfSlipMaster.set("orgSlipNo"	, "C190403-150278B-0001");		// 원전표번호
			dsTcfSlipMaster.set("reasonRev"	, "CF06003");					// 역분개사유코드
		}

		dsTcfSlipMaster.set("procFlag"		, "1");		// 1 : 전표 검증, 2 : 전표생성
	};

