	scwin.btnReverse_onclick = function() {

        svcId = "checkReverseJournalizing";

		dsTcfSlipMaster.reset();
		dsTcfSlipMaster.reform();

		scwin.setMaster();

        ajaxLib.ajax("/cf/cf0520/CF05200000E/checkReverseJournalizing", {
        	mode : "asynchronous"
        	, method : "PUT"
            , requestData: dsTcfSlipMaster.getJSON()
            , callback : "fnCallback"
            , errCallback : "fnErrCallBack"
        });

	};

	scwin.anchor1_onclick = function() {
	    if (input1.getValue() != "") {
			dsSmsReportList.setColumnFilter(
			{ type:'row'
			, colIndex:'callBack'
			, key:input1.getValue()
			, condition:'and'
			, exactMatch:true
			} );

	    	if (input3.getValue() != "") {
				dsSmsReportList.setColumnFilter(
				{ type:'row'
				, colIndex:'callBack'
				, key:input3.getValue()
				, condition:'or'
				, exactMatch:true
				} );
			}

		} else {
			dsSmsReportList.clearFilter();
		}
	};
